import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

// Components
import Navbar from './components/common/Navbar';
import AdminPage from './pages/AdminPage';
import StudentPage from './pages/StudentPage';
import SlotSelection from './components/student/SlotSelection';
import BookingConfirmation from './components/student/BookingConfirmation';
import ErrorPage from './pages/ErrorPage';

// NEW: Auth Components
import Login from './components/auth/Login';
import ProtectedRoute from './components/auth/ProtectedRoute';

// Services
import authService from './services/authService';

// Styles
import './styles/index.css';

function App() {
    return (
        <Router
            future={{
                v7_startTransition: true,
                v7_relativeSplatPath: true
            }}
        >
            <div className="App">
                {/* Conditionally render Navbar only for authenticated admin routes */}
                <ConditionalNavbar />
                
                <Routes>
                    {/* Public Routes */}
                    <Route path="/login" element={<Login />} />
                    
                    {/* Student Routes (Public - accessed via email invitation links) */}
                    <Route path="/student" element={<StudentPage />} />
                    <Route path="/student/select-slot" element={<SlotSelection />} />
                    <Route path="/student/booking-confirmation" element={<BookingConfirmation />} />
                    
                    {/* Protected Admin Routes */}
                    <Route 
                        path="/admin" 
                        element={
                            <ProtectedRoute>
                                <AdminPage />
                            </ProtectedRoute>
                        } 
                    />
                    
                    {/* Default Routes */}
                    <Route path="/" element={<Navigate to="/admin" replace />} />
                    <Route path="/error" element={<ErrorPage />} />
                    <Route path="*" element={<Navigate to="/error" replace />} />
                </Routes>

                <ToastContainer
                    position="top-right"
                    autoClose={5000}
                    hideProgressBar={false}
                    newestOnTop={false}
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover
                    theme="light"
                />
            </div>
        </Router>
    );
}

// Component to conditionally show Navbar
const ConditionalNavbar = () => {
    const currentPath = window.location.pathname;
    
    // Don't show navbar on login page or student pages
    const hideNavbarPaths = ['/login', '/student', '/student/select-slot', '/student/booking-confirmation'];
    
    if (hideNavbarPaths.some(path => currentPath.startsWith(path))) {
        return null;
    }
    
    // Only show navbar if user is authenticated
    if (authService.isAuthenticated() && !authService.isTokenExpired()) {
        return <Navbar />;
    }
    
    return null;
};

export default App;
