  addStudent: async (studentData) => {
    try {
      // Validate required fields (no resume field)
      if (!studentData.name || !studentData.email || !studentData.phone) {
        throw new Error('Name, email, and phone are required');
      }

      const response = await api.post('/students', {
        name: studentData.name.trim(),
        email: studentData.email.trim().toLowerCase(),
        phone: studentData.phone.trim()
        // No resumeLink field
      });
      
      return response.data;
    } catch (error) {
      console.error('Failed to add student:', error);
      throw error;
    }
  },

  // Get student by token (for slot selection)
  getStudentByToken: async (token) => {
    try {
      if (!token) {
        throw new Error('Token is required');
      }
      const response = await api.get(`/students/by-token/${token}`);
      return response.data;
    } catch (error) {
      console.error('Failed to get student by token:', error);
      throw error;
    }
