      }
    }
    
    console.log(`ðŸ“Š Batch download completed: ${successCount} successful, ${failCount} failed`);
    
    return {
      summary: {
        total: students.length,
        successful: successCount,
        failed: failCount
      },
      results
    };
  },

  // NEW: Bulk resume download as ZIP file
  downloadBulkResumesAsZip: async (students) => {
    try {
      if (!Array.isArray(students) || students.length === 0) {
        throw new Error('Students array is required');
      }

      console.log(`ðŸ“¦ Starting ZIP download for ${students.length} students`);

      const studentIds = students.map(s => s.id || s.Id);
      const { blob, filename, size } = await studentService.downloadBulkResumes(studentIds);
      
      console.log(`âœ… ZIP file ready: ${filename} (${studentService.formatFileSize(size)})`);
      
      // Trigger browser download
      const downloadSuccess = studentService.triggerDownload(blob, filename);
      
      if (!downloadSuccess) {
        throw new Error('Failed to trigger ZIP download');
      }
      
      return {
        success: true,
        filename,
        size,
        studentsCount: students.length
      };
      
    } catch (error) {
      console.error('âŒ Bulk ZIP download failed:', error);
      throw error;
    }
  },

  // NEW: Check if student has resume
  hasResume: (student) => {
    return !!(student.ResumeLink || student.resumeLink);
  },

  // NEW: Get students with resumes from selection
  getStudentsWithResumes: (students, selectedIds) => {
    return students.filter(student => 
      selectedIds.includes(student.Id || student.id) && 
      studentService.hasResume(student)
    );
  },

  // NEW: Resume download options for UI
  getDownloadOptions: (selectedStudents, allStudents) => {
    const studentsWithResumes = selectedStudents.filter(id => {
      const student = allStudents.find(s => s.Id === id || s.id === id);
      return student && studentService.hasResume(student);
    });

    return {
      totalSelected: selectedStudents.length,
      withResumes: studentsWithResumes.length,
      withoutResumes: selectedStudents.length - studentsWithResumes.length,
      canDownload: studentsWithResumes.length > 0,
      recommendZip: studentsWithResumes.length > 5 // Recommend ZIP for 5+ files
    };
  }
};

export default studentService;
