import React, { useState, useEffect, useCallback, useMemo } from "react";
import { Table, Badge, Button, Modal, Form, Spinner } from "react-bootstrap";
import interviewService from "../../services/interviewService";
import { toast } from "react-toastify";

const InterviewList = ({ interviews: initialInterviews }) => {
  const [interviews, setInterviews] = useState(initialInterviews || []);
  const [showModal, setShowModal] = useState(false);
  const [selectedInterview, setSelectedInterview] = useState(null);
  const [newStatus, setNewStatus] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (initialInterviews) {
      setInterviews(initialInterviews);
    }
  }, [initialInterviews]);

  const getStatusVariant = useCallback((status) => {
    const variants = {
      Invited: "info",
      Scheduled: "primary",
      Completed: "success",
      Cancelled: "danger",
      "No-Show": "warning",
    };
    return variants[status] || "secondary";
  }, []);

  const handleStatusUpdate = useCallback((interview) => {
    setSelectedInterview(interview);
    setNewStatus(interview.Status);
    setShowModal(true);
  }, []);

  const submitStatusUpdate = async () => {
    try {
      setLoading(true);
      await interviewService.updateStatus(selectedInterview.Id, newStatus);

      setInterviews((prev) =>
        prev.map((interview) =>
          interview.Id === selectedInterview.Id
            ? { ...interview, Status: newStatus }
            : interview
        )
      );

      toast.success("Interview status updated successfully");
      setShowModal(false);
    } catch (error) {
      toast.error("Failed to update interview status");
    } finally {
      setLoading(false);
    }
  };

  const interviewRows = useMemo(
    () =>
      interviews.map((interview) => (
        <tr key={interview.Id}>
          <td className="fw-semibold">{interview.StudentName}</td>
          <td>{interview.StudentEmail}</td>
          <td>
            {interview.SlotDateTime
              ? new Date(interview.SlotDateTime).toLocaleString()
              : "-"}
          </td>
          <td>{interview.Interviewer || "-"}</td>
          <td>
            <Badge bg={getStatusVariant(interview.Status)}>
              {interview.Status}
            </Badge>
          </td>
          <td>
            {interview.MeetingLink ? (
              <a
                href={interview.MeetingLink}
                target="_blank"
                rel="noopener noreferrer"
                className="btn btn-sm btn-outline-primary d-flex align-items-center gap-1"
              >
                <i className="bi bi-camera-video"></i> Join
              </a>
            ) : (
              "-"
            )}
          </td>
          <td className="d-flex flex-wrap gap-2">
            <Button
              size="sm"
              variant="outline-secondary"
              onClick={() => handleStatusUpdate(interview)}
            >
              <i className="bi bi-pencil me-1"></i> Update
            </Button>
            <Button
              size="sm"
              variant="outline-danger"
              onClick={async () => {
                if (
                  window.confirm("Are you sure you want to delete this interview?")
                ) {
                  try {
                    await interviewService.deleteInterview(interview.Id);
                    toast.success("Interview deleted");
                    setInterviews((prev) =>
                      prev.filter((i) => i.Id !== interview.Id)
                    );
                  } catch {
                    toast.error("Failed to delete interview");
                  }
                }
              }}
            >
              <i className="bi bi-trash me-1"></i> Delete
            </Button>
          </td>
        </tr>
      )),
    [interviews, getStatusVariant, handleStatusUpdate]
  );

  return (
    <>
      {/* Header */}
      <div className="d-flex flex-wrap justify-content-between align-items-center mb-3">
        <h5 className="mb-2 mb-md-0 d-flex align-items-center gap-2">
          <i className="bi bi-calendar-check"></i> Scheduled Interviews
        </h5>
        <Badge bg="info" className="fs-6">
          {interviews.length} Total
        </Badge>
      </div>

      {/* Table */}
      <div className="table-responsive shadow-sm rounded">
        <Table striped bordered hover responsive className="align-middle mb-0">
          <thead className="table-dark">
            <tr>
              <th>Student</th>
              <th>Email</th>
              <th>Slot Date & Time</th>
              <th>Interviewer</th>
              <th>Status</th>
              <th>Meeting Link</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {interviews.length === 0 ? (
              <tr>
                <td colSpan="7" className="text-center text-muted py-4">
                  <i className="bi bi-calendar-x fs-4 d-block mb-2"></i>
                  No interviews scheduled yet
                </td>
              </tr>
            ) : (
              interviewRows
            )}
          </tbody>
        </Table>
      </div>

      {/* Status Update Modal */}
      <Modal
        show={showModal}
        onHide={() => setShowModal(false)}
        centered
        backdrop="static"
      >
        <Modal.Header closeButton>
          <Modal.Title>Update Interview Status</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Interview Status</Form.Label>
              <Form.Select
                value={newStatus}
                onChange={(e) => setNewStatus(e.target.value)}
                disabled={loading}
              >
                <option value="Invited">Invited</option>
                <option value="Scheduled">Scheduled</option>
                <option value="Completed">Completed</option>
                <option value="Cancelled">Cancelled</option>
                <option value="No-Show">No-Show</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowModal(false)}
            disabled={loading}
          >
            Cancel
          </Button>
          <Button variant="primary" onClick={submitStatusUpdate} disabled={loading}>
            {loading ? (
              <Spinner size="sm" animation="border" className="me-2" />
            ) : (
              <i className="bi bi-check-circle me-1"></i>
            )}
            Update Status
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default InterviewList;
