  getStudentsWithResumes: (students, selectedIds) => {
    return students.filter(student => 
      selectedIds.includes(student.Id || student.id) && 
      studentService.hasResume(student)
    );
  },

  // Resume download options for UI
  getDownloadOptions: (selectedStudents, allStudents) => {
    const studentsWithResumes = selectedStudents.filter(id => {
      const student = allStudents.find(s => s.Id === id || s.id === id);
      return student && studentService.hasResume(student);
    });

    return {
      totalSelected: selectedStudents.length,
      withResumes: studentsWithResumes.length,
      withoutResumes: selectedStudents.length - studentsWithResumes.length,
      canDownload: studentsWithResumes.length > 0,
      recommendZip: studentsWithResumes.length > 5
    };
  }
};

export default studentService;
