import axios from 'axios';

// Use production URL for deployment, localhost for development
const API_BASE_URL = process.env.REACT_APP_API_URL || 
    (process.env.NODE_ENV === 'production' 
        ? 'https://interviewscheduler.marindrive.in/api' 
        : 'http://localhost:3001/api');

const api = axios.create({
    baseURL: API_BASE_URL,
    timeout: 30000, // Increased from 10s to 30s for better reliability
    headers: {
        'Content-Type': 'application/json',
    },
    // Add retry configuration
    retry: 3,
    retryDelay: 1000,
});

// Add request retry functionality
api.interceptors.response.use(
    (response) => response,
    async (error) => {
        const config = error.config;
        
        // If we haven't already retried and it's a network/timeout error
        if (
            !config.__retryCount && 
            config.retry && 
            (error.code === 'ECONNABORTED' || error.code === 'NETWORK_ERROR' || !error.response)
        ) {
            config.__retryCount = config.__retryCount || 0;
            
            if (config.__retryCount < config.retry) {
                config.__retryCount += 1;
                
                console.log(`ðŸ”„ Retrying request (${config.__retryCount}/${config.retry}):`, config.url);
                
                // Wait before retrying
                await new Promise(resolve => setTimeout(resolve, config.retryDelay));
                
                return api(config);
            }
        }
        
        return Promise.reject(error);
    }
);

// Request interceptor
api.interceptors.request.use(
    (config) => {
        // Add auth token if available
        const token = localStorage.getItem('authToken');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }

        // Add timestamp for request tracking
        config.metadata = { startTime: new Date() };

        // Log request details (only in development)
        if (process.env.NODE_ENV === 'development') {
            console.log(`ðŸŒ ${config.method?.toUpperCase()} ${config.url}`, {
                data: config.data,
                params: config.params,
                headers: config.headers
            });
        }

        return config;
    },
    (error) => {
        console.error('âŒ Request interceptor error:', error);
        return Promise.reject(error);
    }
);

// Response interceptor
api.interceptors.response.use(
    (response) => {
        // Calculate request duration
        const duration = new Date() - response.config.metadata.startTime;
        
        // Log successful responses (only in development)
        if (process.env.NODE_ENV === 'development') {
            console.log(
                `âœ… ${response.config.method?.toUpperCase()} ${response.config.url} - ${response.status} (${duration}ms)`,
                response.data
            );
        }

        return response;
    },
    (error) => {
        // Calculate request duration if metadata exists
        const duration = error.config?.metadata ? 
            new Date() - error.config.metadata.startTime : 'unknown';

        // Enhanced error logging
        const errorInfo = {
            method: error.config?.method?.toUpperCase(),
            url: error.config?.url,
            status: error.response?.status,
            statusText: error.response?.statusText,
            data: error.response?.data,
            message: error.message,
            code: error.code,
            duration: `${duration}ms`
        };

        console.error('âŒ API Error:', errorInfo);

        // Handle specific error cases
        if (error.code === 'ECONNABORTED') {
            console.error('â±ï¸ Request timeout - server may be slow or unreachable');
            error.userMessage = 'Request timeout. Please check your connection and try again.';
        } else if (error.code === 'NETWORK_ERROR' || !error.response) {
            console.error('ðŸŒ Network error - check internet connection');
            error.userMessage = 'Network error. Please check your internet connection.';
        } else if (error.response) {
            // Server responded with error status
            const status = error.response.status;
            const serverError = error.response.data?.error || error.response.statusText;

            switch (status) {
                case 400:
                    error.userMessage = serverError || 'Invalid request. Please check your input.';
                    break;
                case 401:
                    error.userMessage = 'Authentication required. Please log in again.';
                    // Clear auth data and redirect
                    localStorage.removeItem('authToken');
                    // You might want to redirect to login page here
                    break;
                case 403:
                    error.userMessage = 'Access denied. You don\'t have permission for this action.';
                    break;
                case 404:
                    error.userMessage = 'Resource not found. The requested item may no longer exist.';
                    break;
                case 409:
                    error.userMessage = serverError || 'Conflict. This action cannot be completed.';
                    break;
                case 422:
                    error.userMessage = serverError || 'Invalid data provided.';
                    break;
                case 429:
                    error.userMessage = 'Too many requests. Please wait a moment and try again.';
                    break;
                case 500:
                case 502:
                case 503:
                case 504:
                    error.userMessage = 'Server error. Please try again later.';
                    break;
                default:
                    error.userMessage = serverError || 'An unexpected error occurred.';
            }
        }

        // Log critical errors to external service in production
        if (process.env.NODE_ENV === 'production' && error.response?.status >= 500) {
            // You could send to error tracking service like Sentry here
            console.error('ðŸš¨ Critical error logged:', errorInfo);
        }

        return Promise.reject(error);
    }
);

// Helper function to check API health
export const checkAPIHealth = async () => {
    try {
        const response = await api.get('/health');
        return {
            healthy: true,
            data: response.data,
            baseURL: API_BASE_URL
        };
    } catch (error) {
        return {
            healthy: false,
            error: error.message,
            baseURL: API_BASE_URL
        };
    }
};

// Helper function to get API configuration info
export const getAPIInfo = () => {
    return {
        baseURL: API_BASE_URL,
        timeout: api.defaults.timeout,
        environment: process.env.NODE_ENV,
        retryConfig: {
            retry: api.defaults.retry,
            retryDelay: api.defaults.retryDelay
        }
    };
};

// Helper function for manual retry of failed requests
export const retryRequest = async (originalConfig, maxRetries = 3) => {
    let retries = 0;
    
    while (retries < maxRetries) {
        try {
            const response = await api(originalConfig);
            return response;
        } catch (error) {
            retries++;
            
            if (retries >= maxRetries) {
                throw error;
            }
            
            console.log(`ðŸ”„ Manual retry ${retries}/${maxRetries} for:`, originalConfig.url);
            
            // Exponential backoff delay
            await new Promise(resolve => 
                setTimeout(resolve, Math.pow(2, retries) * 1000)
            );
        }
    }
};

export default api;
