import React, { useState } from 'react';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import slotService from '../../services/slotService';

const SlotCreator = ({ onSlotCreated }) => {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        
        try {
            await slotService.createSlot({ slotDateTime: selectedDate });
            setSelectedDate(new Date());
            onSlotCreated();
        } catch (error) {
            setError(error.response?.data?.error || 'Failed to create slot');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Card>
            <Card.Header>
                <h5><i className="bi bi-calendar-plus me-2"></i>Create Interview Slot</h5>
            </Card.Header>
            <Card.Body>
                {error && <Alert variant="danger">{error}</Alert>}
                
                <Form onSubmit={handleSubmit}>
                    <Form.Group className="mb-3">
                        <Form.Label>Select Date & Time</Form.Label>
                        <div>
                            <DatePicker
                                selected={selectedDate}
                                onChange={setSelectedDate}
                                showTimeSelect
                                dateFormat="MMMM d, yyyy h:mm aa"
                                minDate={new Date()}
                                className="form-control"
                                placeholderText="Select date and time"
                            />
                        </div>
                        <Form.Text className="text-muted">
                            Select future date and time for the interview slot
                        </Form.Text>
                    </Form.Group>

                    <div className="d-grid">
                        <Button
                            type="submit"
                            variant="success"
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                    Creating...
                                </>
                            ) : (
                                <>
                                    <i className="bi bi-plus-circle me-2"></i>
                                    Create Slot
                                </>
                            )}
                        </Button>
                    </div>
                </Form>
            </Card.Body>
        </Card>
    );
};

export default SlotCreator;
