import React, { useState, useCallback } from 'react';
import { Card, Form, Button, Alert, Table, Badge } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { PlusCircle, CalendarPlus, Trash } from 'react-bootstrap-icons';
import slotService from '../../services/slotService';

export const SlotCreator = ({ onSlotCreated }) => {
  const [selectedDate, setSelectedDate] = useState(() => new Date());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = useCallback(
    async (e) => {
      e.preventDefault();
      setLoading(true);
      setError('');
      try {
        await slotService.createSlot({ slotDateTime: selectedDate });
        setSelectedDate(new Date());
        onSlotCreated();
      } catch (err) {
        setError(err.response?.data?.error || 'Failed to create slot');
      } finally {
        setLoading(false);
      }
    },
    [selectedDate, onSlotCreated]
  );

  return (
    <Card className="shadow-sm rounded mb-4">
      <Card.Header className="d-flex align-items-center bg-success text-white">
        <CalendarPlus size={24} className="me-2" />
        <h5 className="mb-0">Create Interview Slot</h5>
      </Card.Header>
      <Card.Body>
        {error && (
          <Alert variant="danger" onClose={() => setError('')} dismissible>
            {error}
          </Alert>
        )}

        <Form onSubmit={handleSubmit} noValidate>
          <Form.Group className="mb-3">
            <Form.Label className="fw-semibold" htmlFor="slot-date-picker">
              Select Date &amp; Time
            </Form.Label>
            <DatePicker
              id="slot-date-picker"
              selected={selectedDate}
              onChange={date => setSelectedDate(date || new Date())}
              showTimeSelect
              dateFormat="MMMM d, yyyy h:mm aa"
              minDate={new Date()}
              className="form-control rounded"
              placeholderText="Select date and time"
              autoComplete="off"
              aria-label="Select date and time for interview slot"
              required
            />
          </Form.Group>
          <Form.Text className="text-muted mb-3 d-block">
            Select a future date and time for the interview slot
          </Form.Text>
          <div className="d-grid">
            <Button
              type="submit"
              variant="success"
              disabled={loading}
              aria-label="Create slot"
              className="d-flex align-items-center justify-content-center gap-2 fs-5"
            >
              {loading ? (
                <>
                  <span
                    className="spinner-border spinner-border-sm"
                    role="status"
                    aria-hidden="true"
                  />
                  Creating...
                </>
              ) : (
                <>
                  <PlusCircle />
                  Create Slot
                </>
              )}
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

// ------ SAMPLE RESPONSIVE TABLE COMPONENT BELOW ------

export const AvailableSlotsTable = ({
  slots,
  handleDeleteSlot, // slotId => void
}) => (
  <Card className="shadow-sm rounded">
    <Card.Header className="bg-success text-white fw-semibold">
      Available Slots
    </Card.Header>
    <Card.Body className="p-0">
      <div className="table-responsive">
        <Table hover className="mb-0 align-middle text-nowrap">
          <thead className="table-success">
            <tr>
              <th>Date &amp; Time</th>
              <th>Status</th>
              <th>Booked By</th>
              <th className="text-end" style={{ minWidth: 90 }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {slots.length === 0 ? (
              <tr>
                <td colSpan={4} className="text-center text-muted py-4">
                  No slots available
                </td>
              </tr>
            ) : (
              slots.map(slot => (
                <tr key={slot.Id}>
                  <td style={{ minWidth: 160 }}>
                    {new Date(slot.SlotDateTime).toLocaleString()}
                  </td>
                  <td>
                    <Badge bg={slot.IsBooked ? 'danger' : 'success'}>
                      {slot.IsBooked ? 'Booked' : 'Available'}
                    </Badge>
                  </td>
                  <td>{slot.IsBooked ? slot.StudentName : '-'}</td>
                  <td className="text-end">
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleDeleteSlot(slot.Id)}
                      aria-label={`Delete slot for ${slot.StudentName || 'empty'}`}
                    >
                      <Trash />
                    </Button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </Table>
      </div>
    </Card.Body>
  </Card>
);


export default SlotCreator;
