import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Row, 
  Col, 
  Card, 
  Form, 
  Button, 
  Alert, 
  Spinner,
  InputGroup
} from 'react-bootstrap';
import { 
  PersonFill, 
  LockFill, 
  EyeFill, 
  EyeSlashFill,
  ShieldLockFill 
} from 'react-bootstrap-icons';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import authService from '../../services/authService';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  
  const navigate = useNavigate();

  // Redirect if already authenticated
  useEffect(() => {
    console.log('üîç Login component mounted - checking authentication...');
    
    const isAuthenticated = authService.isAuthenticated();
    const isTokenExpired = authService.isTokenExpired();
    
    console.log('üîê Login page auth check:', { 
      isAuthenticated, 
      isTokenExpired,
      hasToken: !!localStorage.getItem('admin_token'),
      hasAdminInfo: !!localStorage.getItem('admin_info')
    });
    
    if (isAuthenticated && !isTokenExpired) {
      console.log('‚úÖ Already authenticated, redirecting to admin...');
      navigate('/admin');
    } else {
      console.log('‚ùå Not authenticated or token expired, staying on login page');
      // Clear any invalid tokens
      if (isTokenExpired) {
        console.log('üßπ Clearing expired tokens...');
        authService.logout();
      }
    }
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    console.log('üîê Login attempt starting...');
    
    if (!formData.username || !formData.password) {
      setError('Please enter both username and password');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      console.log('üì° Calling authService.login...');
      const result = await authService.login(formData);
      
      console.log('üîê Login result:', {
        success: result.success,
        hasToken: !!result.token,
        adminUsername: result.admin?.Username,
        message: result.message
      });
      
      if (result.success) {
        console.log('‚úÖ LOGIN SUCCESS! Checking storage...');
        
        // Verify token was stored
        const storedToken = localStorage.getItem('admin_token');
        const storedAdminInfo = localStorage.getItem('admin_info');
        
        console.log('üìä Post-login storage check:', {
          tokenStored: !!storedToken,
          tokenLength: storedToken?.length,
          adminInfoStored: !!storedAdminInfo,
          adminInfo: storedAdminInfo ? JSON.parse(storedAdminInfo) : null
        });
        
        // Immediate auth check
        const immediateAuthCheck = {
          isAuthenticated: authService.isAuthenticated(),
          isTokenExpired: authService.isTokenExpired()
        };
        
        console.log('üîç Immediate auth check after login:', immediateAuthCheck);
        
        toast.success(`Welcome back, ${result.admin.Username}!`);
        
        // Small delay to ensure storage is complete
        setTimeout(() => {
          console.log('üöÄ Navigating to /admin...');
          navigate('/admin');
        }, 100);
        
      } else {
        console.log('‚ùå Login failed:', result.message);
        setError(result.message);
      }
    } catch (error) {
      console.error('‚ùå Login error:', error);
      setError('Network error. Please check your connection and try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-vh-100 d-flex align-items-center bg-light">
      <Container>
        <Row className="justify-content-center">
          <Col xs={12} sm={8} md={6} lg={4}>
            <Card className="shadow-lg border-0">
              <Card.Header className="bg-primary text-white text-center py-4">
                <div className="mb-2">
                  <ShieldLockFill size={48} />
                </div>
                <h3 className="mb-0 fw-bold">Admin Login</h3>
                <p className="mb-0 opacity-75">B2World Interview System</p>
              </Card.Header>
              
              <Card.Body className="p-4">
                {/* DEBUG INFO - Remove in production */}
                {process.env.NODE_ENV === 'development' && (
                  <Alert variant="info" className="mb-3">
                    <small>
                      <strong>Debug Info:</strong><br/>
                      Auth: {authService.isAuthenticated() ? '‚úÖ' : '‚ùå'} | 
                      Expired: {authService.isTokenExpired() ? '‚ùå' : '‚úÖ'} | 
                      Token: {localStorage.getItem('admin_token') ? '‚úÖ' : '‚ùå'}
                    </small>
                  </Alert>
                )}

                {error && (
                  <Alert variant="danger" className="mb-3">
                    <small>{error}</small>
                  </Alert>
                )}

                <Form onSubmit={handleSubmit}>
                  <Form.Group className="mb-3">
                    <Form.Label>Username or Email</Form.Label>
                    <InputGroup>
                      <InputGroup.Text>
                        <PersonFill />
                      </InputGroup.Text>
                      <Form.Control
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        placeholder="Enter username or email"
                        required
                        autoComplete="username"
                        disabled={isLoading}
                      />
                    </InputGroup>
                  </Form.Group>

                  <Form.Group className="mb-4">
                    <Form.Label>Password</Form.Label>
                    <InputGroup>
                      <InputGroup.Text>
                        <LockFill />
                      </InputGroup.Text>
                      <Form.Control
                        type={showPassword ? "text" : "password"}
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        placeholder="Enter password"
                        required
                        autoComplete="current-password"
                        disabled={isLoading}
                      />
                      <Button
                        variant="outline-secondary"
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        disabled={isLoading}
                      >
                        {showPassword ? <EyeSlashFill /> : <EyeFill />}
                      </Button>
                    </InputGroup>
                  </Form.Group>

                  <div className="d-grid">
                    <Button
                      variant="primary"
                      type="submit"
                      size="lg"
                      disabled={isLoading}
                      className="fw-bold"
                    >
                      {isLoading ? (
                        <>
                          <Spinner
                            as="span"
                            animation="border"
                            size="sm"
                            className="me-2"
                          />
                          Signing In...
                        </>
                      ) : (
                        'Sign In'
                      )}
                    </Button>
                  </div>
                </Form>

                {/* Quick test buttons - Remove in production */}
                {process.env.NODE_ENV === 'development' && (
                  <div className="mt-3 pt-3 border-top">
                    <small className="text-muted d-block mb-2">Debug Actions:</small>
                    <div className="d-flex gap-2 flex-wrap">
                      <Button 
                        size="sm" 
                        variant="outline-secondary"
                        onClick={() => {
                          console.log('üîç Manual auth check:', {
                            isAuthenticated: authService.isAuthenticated(),
                            isTokenExpired: authService.isTokenExpired(),
                            token: localStorage.getItem('admin_token'),
                            adminInfo: localStorage.getItem('admin_info')
                          });
                        }}
                      >
                        Check Auth
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline-danger"
                        onClick={() => {
                          authService.logout();
                          console.log('üßπ Storage cleared');
                          window.location.reload();
                        }}
                      >
                        Clear Storage
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline-info"
                        onClick={async () => {
                          try {
                            const response = await fetch('http://localhost:3001/api/test-auth', {
                              headers: {
                                'Authorization': `Bearer ${localStorage.getItem('admin_token')}`
                              }
                            });
                            const data = await response.json();
                            console.log('üß™ API Test Result:', data);
                            toast.info(`API Test: ${data.success ? 'Success' : 'Failed'}`);
                          } catch (error) {
                            console.error('üß™ API Test Error:', error);
                            toast.error('API Test Failed');
                          }
                        }}
                      >
                        Test API
                      </Button>
                    </div>
                  </div>
                )}
              </Card.Body>
              
              <Card.Footer className="text-center text-muted py-3">
                <small>¬© 2025 B2World (BTOW Pvt. Ltd.)</small>
