import React, { useState, useEffect, useCallback } from 'react';
import {
  Container,
  Row,
  Col,
  Tab,
  Tabs,
  Button,
  Card,
  Form,
  Alert,
  Badge,
  Table,
} from 'react-bootstrap';
import { Trash, PersonCheck } from 'react-bootstrap-icons';
import { toast } from 'react-toastify';
import StudentForm from './StudentForm';
import ExcelUpload from './ExcelUpload';
import SlotCreator from './SlotCreator';
import InterviewList from './InterviewList';
import studentService from '../../services/studentService';
import slotService from '../../services/slotService';
import interviewService from '../../services/interviewService';

const AdminDashboard = () => {
  const [students, setStudents] = useState([]);
  const [slots, setSlots] = useState([]);
  const [interviews, setInterviews] = useState([]);
  const [selectedStudents, setSelectedStudents] = useState([]);

  // Load data function, memoized to prevent re-creation
  const loadData = useCallback(async () => {
    try {
      const [studentsData, slotsData, interviewsData] = await Promise.all([
        studentService.getStudents(),
        slotService.getSlots(),
        interviewService.getInterviews(),
      ]);
      setStudents(studentsData);
      setSlots(slotsData);
      setInterviews(interviewsData);
    } catch (error) {
      toast.error('Failed to load data');
      console.error('Load data error:', error);
    }
  }, []);

  useEffect(() => {
    loadData();
  }, [loadData]);

  // Handler functions keeping minimal re-renders using useCallback
  const handleStudentAdded = useCallback(() => {
    loadData();
    toast.success('Student added successfully');
  }, [loadData]);

  const handleExcelUploaded = useCallback(
    (result) => {
      loadData();
      toast.success(`Excel processed: ${result.successful} successful, ${result.failed} failed`);
    },
    [loadData]
  );

  const handleSlotCreated = useCallback(() => {
    loadData();
    toast.success('Interview slot created successfully');
  }, [loadData]);

  const handleSendInvitations = async () => {
    if (selectedStudents.length === 0) {
      toast.warning('Please select students to send invitations');
      return;
    }
    try {
      await studentService.sendInvitations(selectedStudents);
      toast.success('Invitations sent successfully');
      setSelectedStudents([]);
    } catch {
      toast.error('Failed to send invitations');
    }
  };

  // Toggle select all students
  const handleSelectAll = (checked) => {
    if (checked) {
      setSelectedStudents(students.map((s) => s.Id));
    } else {
      setSelectedStudents([]);
    }
  };

  // Toggle select individual student
  const handleStudentSelect = (id, checked) => {
    if (checked) {
      setSelectedStudents((prev) => [...prev, id]);
    } else {
      setSelectedStudents((prev) => prev.filter((sid) => sid !== id));
    }
  };

  return (
    <Container fluid className="py-4">
      <Row className="mb-4">
        <Col>
          <h2 className="text-primary fw-bold">Interview Scheduler - Admin Dashboard</h2>
        </Col>
      </Row>

      <Tabs
        defaultActiveKey="students"
        className="mb-4 shadow-sm rounded"
        fill
        justify
        id="admin-dashboard-tabs"
      >
        <Tab eventKey="students" title="Student Management" tabClassName="fs-6 fw-semibold p-2">
          <Row className="g-4">
            <Col xs={12} md={6}>
              <StudentForm onStudentAdded={handleStudentAdded} />
            </Col>
            <Col xs={12} md={6}>
              <ExcelUpload onExcelUploaded={handleExcelUploaded} />
            </Col>
          </Row>

          <Row className="mt-5">
            <Col xs={12}>
              <Card className="shadow-sm rounded">
                <Card.Header className="bg-info d-flex justify-content-between align-items-center text-white fw-semibold">
                  <h5 className="mb-0">Students List</h5>
                  <Button
                    variant="light"
                    className="fw-semibold"
                    onClick={handleSendInvitations}
                    disabled={selectedStudents.length === 0}
                    aria-label="Send Invitations"
                  >
                    <PersonCheck className="me-1" />
                    Send Invitations ({selectedStudents.length})
                  </Button>
                </Card.Header>
                <Card.Body className="p-0">
                  <div className="table-responsive">
                    <Table striped hover responsive className="mb-0 align-middle">
                      <thead className="table-info">
                        <tr>
                          <th style={{ width: '3rem' }}>
                            <Form.Check
                              type="checkbox"
                              id="selectAllStudents"
                              onChange={(e) => handleSelectAll(e.target.checked)}
                              checked={students.length > 0 && selectedStudents.length === students.length}
                              aria-label="Select all students"
                            />
                          </th>
                          <th>Name</th>
                          <th>Email</th>
                          <th>Phone</th>
                          <th>Resume</th>
                          <th style={{ width: '100px' }}>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {students.length === 0 ? (
                          <tr>
                            <td colSpan={6} className="text-center py-4 text-muted">
                              No students found
                            </td>
                          </tr>
                        ) : (
                          students.map((student) => (
                            <tr key={student.Id}>
                              <td>
                                <Form.Check
                                  type="checkbox"
                                  id={`student-${student.Id}`}
                                  checked={selectedStudents.includes(student.Id)}
                                  onChange={(e) =>
                                    handleStudentSelect(student.Id, e.target.checked)
                                  }
                                  aria-label={`Select student ${student.Name}`}
                                />
                              </td>
                              <td>{student.Name}</td>
                              <td>{student.Email}</td>
                              <td>{student.Phone || '-'}</td>
                              <td>
                                {student.ResumeLink ? (
                                  <a
                                    href={student.ResumeLink}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="text-decoration-none"
                                    aria-label={`View resume of ${student.Name}`}
                                  >
                                    View Resume
                                  </a>
                                ) : (
                                  '-'
                                )}
                              </td>
                              <td>
                                <Button
                                  variant="danger"
                                  size="sm"
                                  onClick={async () => {
                                    if (
                                      window.confirm(
                                        'Are you sure you want to delete this student?'
                                      )
                                    ) {
                                      try {
                                        await studentService.deleteStudent(student.Id);
                                        toast.success('Student deleted successfully');
                                        loadData();
                                      } catch (error) {
                                        const backendError = error.response?.data?.error;
                                        toast.error(backendError || 'Failed to delete student');
                                      }
                                    }
                                  }}
                                  aria-label={`Delete student ${student.Name}`}
                                >
                                  <Trash />
                                </Button>
                              </td>
                            </tr>
                          ))
                        )}
                      </tbody>
                    </Table>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Tab>

        <Tab eventKey="slots" title="Interview Slots" tabClassName="fs-6 fw-semibold p-2">
          <Row className="g-4">
            <Col xs={12} md={4}>
              <SlotCreator onSlotCreated={handleSlotCreated} />
            </Col>
            <Col xs={12} md={8}>
              <Card className="shadow-sm rounded">
                <Card.Header className="bg-success text-white fw-semibold">Available Slots</Card.Header>
                <Card.Body className="p-0">
                  <div className="table-responsive">
                    <Table striped hover responsive className="mb-0 align-middle">
                      <thead className="table-success">
                        <tr>
                          <th>Date &amp; Time</th>
                          <th>Status</th>
                          <th>Booked By</th>
                          <th style={{ width: '100px' }}>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {slots.length === 0 ? (
                          <tr>
                            <td colSpan={4} className="text-center py-4 text-muted">
                              No slots available
                            </td>
                          </tr>
                        ) : (
                          slots.map((slot) => (
                            <tr key={slot.Id}>
                              <td>{new Date(slot.SlotDateTime).toLocaleString()}</td>
                              <td>
                                <Badge
                                  bg={slot.IsBooked ? 'danger' : 'success'}
                                  className="py-1"
                                >
                                  {slot.IsBooked ? 'Booked' : 'Available'}
                                </Badge>
                              </td>
                              <td>{slot.IsBooked ? slot.StudentName : '-'}</td>
                              <td>
                                <Button
                                  variant="danger"
                                  size="sm"
                                  onClick={async () => {
                                    if (
                                      window.confirm('Are you sure you want to delete this slot?')
                                    ) {
                                      try {
                                        await slotService.deleteSlot(slot.Id);
                                        toast.success('Slot deleted');
                                        loadData();
                                      } catch {
                                        toast.error('Failed to delete slot');
                                      }
                                    }
                                  }}
                                  aria-label={`Delete slot on ${new Date(
                                    slot.SlotDateTime
                                  ).toLocaleString()}`}
                                >
                                  <Trash />
                                </Button>
                              </td>
                            </tr>
                          ))
                        )}
                      </tbody>
                    </Table>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Tab>

        <Tab eventKey="interviews" title="Scheduled Interviews" tabClassName="fs-6 fw-semibold p-2">
          <InterviewList interviews={interviews} />
        </Tab>
      </Tabs>
    </Container>
  );
};

export default AdminDashboard;
