import React, { useState, useEffect } from 'react';
import { 
  Users, 
  Calendar, 
  FileSpreadsheet, 
  Plus, 
  Search, 
  Filter, 
  Mail, 
  Trash2, 
  Eye, 
  Edit3, 
  VideoIcon,
  CheckCircle,
  XCircle,
  Clock,
  AlertTriangle,
  Download,
  Upload,
  UserPlus,
  CalendarPlus
} from 'lucide-react';
// Main AdminDashboard Component
const AdminDashboard = () => {
  const [students, setStudents] = useState([]);
  const [slots, setSlots] = useState([]);
  const [interviews, setInterviews] = useState([]);
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [activeTab, setActiveTab] = useState('students');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [studentsData, slotsData, interviewsData] = await Promise.all([
        mockServices.studentService.getStudents(),
        mockServices.slotService.getSlots(),
        mockServices.interviewService.getInterviews()
      ]);
      setStudents(studentsData);
      setSlots(slotsData);
      setInterviews(interviewsData);
    } catch (error) {
      toast.error('Failed to load data');
    }
  };

  const handleStudentAdded = () => {
    loadData();
    toast.success('Student added successfully');
  };

  const handleExcelUploaded = (result) => {
    loadData();
    toast.success(`Excel processed: ${result.successful} successful, ${result.failed} failed`);
  };

  const handleSlotCreated = () => {
    loadData();
    toast.success('Interview slot created successfully');
  };

  const handleSendInvitations = async () => {
    if (selectedStudents.length === 0) {
      toast.warning('Please select students to send invitations');
      return;
    }
    try {
      await mockServices.studentService.sendInvitations(selectedStudents);
      toast.success('Invitations sent successfully');
      setSelectedStudents([]);
    } catch (error) {
      toast.error('Failed to send invitations');
    }
  };

  const filteredStudents = students.filter(student =>
    student.Name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.Email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const tabs = [
    { id: 'students', label: 'Student Management', icon: Users, count: students.length },
    { id: 'slots', label: 'Interview Slots', icon: Calendar, count: slots.length },
    { id: 'interviews', label: 'Scheduled Interviews', icon: VideoIcon, count: interviews.length }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Interview Scheduler</h1>
          <p className="text-gray-600">Admin Dashboard - Manage students, slots, and interviews</p>
        </div>

        {/* Tabs */}
        <div className="mb-8">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8 overflow-x-auto">
              {tabs.map((tab) => {
                const TabIcon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm flex items-center gap-2 transition-colors ${
                      activeTab === tab.id
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    <TabIcon className="w-5 h-5" />
                    {tab.label}
                    <span className="bg-gray-100 text-gray-600 py-0.5 px-2 rounded-full text-xs">
                      {tab.count}
                    </span>
                  </button>
                );
              })}
            </nav>
          </div>
        </div>

        {/* Tab Content */}
        {activeTab === 'students' && (
          <div className="space-y-8">
            {/* Add Student and Excel Upload */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <StudentForm onStudentAdded={handleStudentAdded} />
              <ExcelUpload onExcelUploaded={handleExcelUploaded} />
            </div>

            {/* Students List */}
            <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
              <div className="px-6 py-4 border-b border-gray-200">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
                  <div className="flex items-center gap-3">
                    <div className="p-2 bg-blue-100 rounded-lg">
                      <Users className="w-6 h-6 text-blue-600" />
                    </div>
                    <div>
                      <h3 className="text-xl font-semibold text-gray-900">Students List</h3>
                      <p className="text-gray-600">{students.length} total students</p>
                    </div>
                  </div>
                  
                  <div className="flex flex-col sm:flex-row gap-3 w-full sm:w-auto">
                    <div className="relative">
                      <Search className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
                      <input
                        type="text"
                        placeholder="Search students..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 w-full sm:w-64"
                      />
                    </div>
                    
                    <button 
                      onClick={handleSendInvitations}
                      disabled={selectedStudents.length === 0}
                      className="px-4 py-2 bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-lg hover:from-blue-700 hover:to-blue-800 transition-all duration-200 disabled:opacity-50 font-medium flex items-center gap-2"
                    >
                      <Mail className="w-4 h-4" />
                      Send Invitations ({selectedStudents.length})
                    </button>
                  </div>
                </div>
              </div>

              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-4 text-left">
                        <input
                          type="checkbox"
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedStudents(filteredStudents.map(s => s.Id));
                            } else {
                              setSelectedStudents([]);
                            }
                          }}
                          checked={selectedStudents.length === filteredStudents.length && filteredStudents.length > 0}
                          className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                        />
                      </th>
                      <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Info</th>
                      <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider hidden sm:table-cell">Contact</th>
                      <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider hidden lg:table-cell">Resume</th>
                      <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-200">
                    {filteredStudents.length === 0 ? (
                      <tr>
                        <td colSpan="5" className="px-6 py-12 text-center text-gray-500">
                          <Users className="w-12 h-12 text-gray-300 mx-auto mb-3" />
                          <p>No students found</p>
                        </td>
                      </tr>
                    ) : (
                      filteredStudents.map(student => (
                        <tr key={student.Id} className="hover:bg-gray-50 transition-colors">
                          <td className="px-6 py-4">
                            <input
                              type="checkbox"
                              checked={selectedStudents.includes(student.Id)}
                              onChange={(e) => {
                                if (e.target.checked) {
                                  setSelectedStudents([...selectedStudents, student.Id]);
                                } else {
                                  setSelectedStudents(selectedStudents.filter(id => id !== student.Id));
                                }
                              }}
                              className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                            />
                          </td>
                          <td className="px-6 py-4">
                            <div>
                              <div className="font-medium text-gray-900">{student.Name}</div>
                              <div className="text-sm text-gray-500 sm:hidden">{student.Email}</div>
                            </div>
                          </td>
                          <td className="px-6 py-4 hidden sm:table-cell">
                            <div>
                              <div className="text-sm text-gray-900">{student.Email}</div>
                              <div className="text-sm text-gray-500">{student.Phone || '-'}</div>
                            </div>
                          </td>
                          <td className="px-6 py-4 hidden lg:table-cell">
                            {student.ResumeLink ? (
                              <a 
                                href={student.ResumeLink} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="inline-flex items-center gap-2 px-3 py-2 bg-green-100 text-green-700 rounded-lg hover:bg-green-200 transition-colors text-sm font-medium"
                              >
                                <Eye className="w-4 h-4" />
                                View Resume
                              </a>
                            ) : (
                              <span className="text-gray-400">No resume</span>
                            )}
                          </td>
                          <td className="px-6 py-4">
                            <button
                              onClick={async () => {
                                if (window.confirm('Are you sure you want to delete this student?')) {
                                  try {
                                    await mockServices.studentService.deleteStudent(student.Id);
                                    toast.success('Student deleted successfully');
                                    loadData();
                                  } catch (error) {
                                    toast.error('Failed to delete student');
                                  }
                                }
                              }}
                              className="p-2 text-gray-600 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                              title="Delete Student"
                            >
                              <Trash2 className="w-4 h-4" />
                            </button>
                          </td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'slots' && (
          <div className="space-y-8">
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              <SlotCreator onSlotCreated={handleSlotCreated} />
              
              <div className="lg:col-span-2">
                <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
                  <div className="px-6 py-4 border-b border-gray-200">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-purple-100 rounded-lg">
                        <Calendar className="w-6 h-6 text-purple-600" />
                      </div>
                      <div>
                        <h3 className="text-xl font-semibold text-gray-900">Available Slots</h3>
                        <p className="text-gray-600">{slots.length} total slots</p>
                      </div>
                    </div>
                  </div>

                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date & Time</th>
                          <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                          <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider hidden sm:table-cell">Booked By</th>
                          <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                      </thead>
                      <tbody className="divide-y divide-gray-200">
                        {slots.length === 0 ? (
                          <tr>
                            <td colSpan="4" className="px-6 py-12 text-center text-gray-500">
                              <Calendar className="w-12 h-12 text-gray-300 mx-auto mb-3" />
                              <p>No interview slots created yet</p>
                            </td>
                          </tr>
                        ) : (
                          slots.map(slot => (
                            <tr key={slot.Id} className="hover:bg-gray-50 transition-colors">
                              <td className="px-6 py-4">
                                <div>
                                  <div className="font-medium text-gray-900">
                                    {new Date(slot.SlotDateTime).toLocaleDateString()}
                                  </div>
                                  <div className="text-sm text-gray-500">
                                    {new Date(slot.SlotDateTime).toLocaleTimeString()}
                                  </div>
                                </div>
                              </td>
                              <td className="px-6 py-4">
                                <span className={`inline-flex items-center gap-1 px-3 py-1 rounded-full text-xs font-medium ${
                                  slot.IsBooked 
                                    ? 'bg-red-100 text-red-800' 
                                    : 'bg-green-100 text-green-800'
                                }`}>
                                  {slot.IsBooked ? (
                                    <>
                                      <XCircle className="w-3 h-3" />
                                      Booked
                                    </>
                                  ) : (
                                    <>
                                      <CheckCircle className="w-3 h-3" />
                                      Available
                                    </>
                                  )}
                                </span>
                              </td>
                              <td className="px-6 py-4 hidden sm:table-cell">
                                <span className="text-sm text-gray-900">
                                  {slot.IsBooked ? slot.StudentName : '-'}
                                </span>
                              </td>
                              <td className="px-6 py-4">
                                <button 
                                  onClick={async () => {
                                    if(window.confirm('Are you sure you want to delete this slot?')){
                                      try {
                                        await mockServices.slotService.deleteSlot(slot.Id);
                                        toast.success('Slot deleted');
                                        loadData();
                                      } catch {
                                        toast.error('Failed to delete slot');
                                      }
                                    }
                                  }}
                                  className="p-2 text-gray-600 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                                  title="Delete Slot"
                                >
                                  <Trash2 className="w-4 h-4" />
                                </button>
                              </td>
                            </tr>
                          ))
                        )}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'interviews' && (
          <InterviewList interviews={interviews} />
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
