  },
  // âž¤ Download resume by student ID - ENHANCED WITH STUDENT NAME
  downloadResume: async (req, res) => {
    try {
      const {
        studentId
      } = req.params;
      console.log('ðŸ“„ Resume download request for student:', studentId);
      if (!studentId) {
        return res.status(400).json({
          error: 'Student ID is required'
        });
      }
      const pool = await getPool();

      // Get student and resume info
      const result = await pool.request().input('studentId', sql.Int, parseInt(studentId)).query(`
          SELECT Id, Name, Email, ResumeLink, ResumeFileName 
          FROM Students 
          WHERE Id = @studentId AND ResumeLink IS NOT NULL
        `);
      if (result.recordset.length === 0) {
        return res.status(404).json({
          error: 'Student not found or no resume uploaded'
        });
      }
      const student = result.recordset[0];
      let resumePath = student.ResumeLink;

      // Handle different path formats
      if (student.ResumeLink.startsWith('http')) {
        // If it's a full URL, extract the filename
        const urlParts = student.ResumeLink.split('/');
        const filename = urlParts[urlParts.length - 1];
        resumePath = path.join(__dirname, '../uploads', filename);
      } else if (!path.isAbsolute(student.ResumeLink)) {
        // If it's a relative path, make it absolute
        resumePath = path.join(__dirname, '..', student.ResumeLink);
      }

      // Check if file exists
      if (!fs.existsSync(resumePath)) {
        console.error('âŒ Resume file not found:', resumePath);
        return res.status(404).json({
          error: 'Resume file not found on server'
        });
      }

      // Get file stats and extension
      const stats = fs.statSync(resumePath);
      const fileExtension = path.extname(resumePath).toLowerCase();

      // Set appropriate content type
      let contentType = 'application/octet-stream';
      switch (fileExtension) {
        case '.pdf':
          contentType = 'application/pdf';
          break;
        case '.doc':
          contentType = 'application/msword';
          break;
        case '.docx':
          contentType = 'application/vnd.openxmlformats-officedocument.wordprocessingml.document';
          break;
      }

      // Generate clean student name for filename
      const cleanStudentName = student.Name.trim().replace(/[^a-zA-Z0-9\s]/g, '') // Remove special characters
      .replace(/\s+/g, '_') // Replace spaces with underscores
      .substring(0, 50); // Limit length to 50 characters

      // Create filename with student name and timestamp
      const timestamp = new Date().toISOString().split('T')[0]; // YYYY-MM-DD format
      const downloadFilename = `${cleanStudentName}_Resume_${timestamp}${fileExtension}`;

      // Set headers for download
      res.setHeader('Content-Type', contentType);
      res.setHeader('Content-Length', stats.size);
      res.setHeader('Content-Disposition', `attachment; filename="${downloadFilename}"`);
      res.setHeader('Cache-Control', 'no-cache, no-store, must-revalidate');
      res.setHeader('Pragma', 'no-cache');
      res.setHeader('Expires', '0');
      console.log(`ðŸ“¥ Serving resume download: ${downloadFilename} (${stats.size} bytes) for ${student.Name}`);

      // Stream the file
      const fileStream = fs.createReadStream(resumePath);
      fileStream.pipe(res);
      fileStream.on('error', error => {
        console.error('âŒ Error streaming file:', error);
        if (!res.headersSent) {
          res.status(500).json({
            error: 'Failed to download resume'
          });
        }
      });
      fileStream.on('end', () => {
        console.log('âœ… Resume download completed for:', student.Name);
      });
    } catch (error) {
      console.error('âŒ Error downloading resume:', error);
      if (!res.headersSent) {
        res.status(500).json({
          error: 'Failed to download resume'
        });
      }
    }
  },
  // âž¤ NEW: Bulk download resumes by student IDs
  downloadBulkResumes: async (req, res) => {
    try {
      const {
        studentIds
      } = req.body;
      if (!Array.isArray(studentIds) || studentIds.length === 0) {
        return res.status(400).json({
          error: 'Student IDs array is required'
        });
      }
      console.log('ðŸ“¦ Bulk resume download request for', studentIds.length, 'students');
      const pool = await getPool();
      const request = pool.request();

      // Build parameterized query inputs
      studentIds.forEach((id, index) => {
        request.input(`id${index}`, sql.Int, parseInt(id));
      });
      const query = `
        SELECT Id, Name, Email, ResumeLink, ResumeFileName 
        FROM Students
        WHERE Id IN (${studentIds.map((_, i) => `@id${i}`).join(',')})
        AND ResumeLink IS NOT NULL
      `;
      const result = await request.query(query);
      if (result.recordset.length === 0) {
        return res.status(404).json({
          error: 'No students with resumes found'
        });
      }

      // Create a zip file for bulk download
      const archiver = require('archiver');
      const archive = archiver('zip', {
        zlib: {
          level: 9
        }
      });
      const timestamp = new Date().toISOString().split('T')[0];
      const zipFilename = `Student_Resumes_${timestamp}.zip`;
      res.setHeader('Content-Type', 'application/zip');
      res.setHeader('Content-Disposition', `attachment; filename="${zipFilename}"`);
      archive.pipe(res);
      let filesAdded = 0;
      let filesSkipped = 0;
      for (const student of result.recordset) {
        try {
          let resumePath = student.ResumeLink;

          // Handle different path formats
          if (student.ResumeLink.startsWith('http')) {
            const urlParts = student.ResumeLink.split('/');
            const filename = urlParts[urlParts.length - 1];
            resumePath = path.join(__dirname, '../uploads', filename);
          } else if (!path.isAbsolute(student.ResumeLink)) {
            resumePath = path.join(__dirname, '..', student.ResumeLink);
          }

          // Check if file exists
          if (fs.existsSync(resumePath)) {
            const fileExtension = path.extname(resumePath).toLowerCase();
            const cleanStudentName = student.Name.trim().replace(/[^a-zA-Z0-9\s]/g, '').replace(/\s+/g, '_').substring(0, 50);
            const archiveFilename = `${cleanStudentName}_Resume${fileExtension}`;
            archive.file(resumePath, {
              name: archiveFilename
            });
            filesAdded++;
            console.log(`ðŸ“„ Added to archive: ${archiveFilename}`);
          } else {
            console.warn(`âš ï¸ Resume file not found for ${student.Name}: ${resumePath}`);
            filesSkipped++;
          }
        } catch (fileError) {
          console.error(`âŒ Error processing resume for ${student.Name}:`, fileError.message);
          filesSkipped++;
        }
      }
      archive.finalize();
      archive.on('end', () => {
        console.log(`âœ… Bulk download completed: ${filesAdded} files added, ${filesSkipped} files skipped`);
      });
      archive.on('error', error => {
        console.error('âŒ Error creating zip archive:', error);
        if (!res.headersSent) {
          res.status(500).json({
            error: 'Failed to create resume archive'
          });
        }
      });
    } catch (error) {
      console.error('âŒ Error in bulk download:', error);
      if (!res.headersSent) {
        res.status(500).json({
          error: 'Failed to process bulk download'
        });
      }
    }
  },
