  // âž¤ Bulk delete students by date range
  bulkDeleteByDate: async (req, res) => {
    try {
      const {
        start,
        end
      } = req.query;
      if (!start || !end) {
        return res.status(400).json({
          error: 'Missing start or end date'
        });
      }
      const pool = await getPool();

      // First check how many students would be affected
      const checkResult = await pool.request().input('start', sql.DateTime2, new Date(start)).input('end', sql.DateTime2, new Date(end)).query(`
          SELECT COUNT(*) as TotalCount,
          COUNT(i.Id) as WithInterviews
          FROM Students s
          LEFT JOIN Interviews i ON s.Id = i.StudentId
          WHERE s.CreatedAt BETWEEN @start AND @end
        `);
      const {
        TotalCount,
        WithInterviews
      } = checkResult.recordset[0];
      if (WithInterviews > 0) {
        return res.status(400).json({
          error: `Cannot delete students with interviews. ${WithInterviews} out of ${TotalCount} students have interview records.`,
          studentsWithInterviews: WithInterviews,
          totalStudents: TotalCount
        });
      }

      // Get resume files to clean up
      const resumeFiles = await pool.request().input('start', sql.DateTime2, new Date(start)).input('end', sql.DateTime2, new Date(end)).query(`
          SELECT ResumeLink FROM Students 
          WHERE CreatedAt BETWEEN @start AND @end 
          AND ResumeLink IS NOT NULL
          AND Id NOT IN (SELECT DISTINCT StudentId FROM Interviews WHERE StudentId IS NOT NULL)
        `);

      // Proceed with deletion if no interviews found
      const result = await pool.request().input('start', sql.DateTime2, new Date(start)).input('end', sql.DateTime2, new Date(end)).query(`
          DELETE FROM Students 
          WHERE CreatedAt BETWEEN @start AND @end
          AND Id NOT IN (SELECT DISTINCT StudentId FROM Interviews WHERE StudentId IS NOT NULL)
        `);

      // Clean up resume files
      let filesDeleted = 0;
      for (const row of resumeFiles.recordset) {
        try {
          let resumePath = row.ResumeLink;
          if (!path.isAbsolute(resumePath)) {
            resumePath = path.join(__dirname, '..', resumePath);
          }
          if (fs.existsSync(resumePath)) {
            fs.unlinkSync(resumePath);
            filesDeleted++;
          }
        } catch (fileError) {
          console.warn('âš ï¸ Could not delete resume file:', fileError.message);
        }
      }
      console.log(`ðŸ—‘ï¸ Bulk deleted ${result.rowsAffected[0]} students and ${filesDeleted} resume files from ${start} to ${end}`);
      res.json({
        message: 'Students deleted successfully',
        deletedCount: result.rowsAffected[0],
        filesDeleted,
        dateRange: {
          start,
          end
        }
      });
    } catch (error) {
      console.error('âŒ Error in bulkDeleteByDate:', error);
      res.status(500).json({
        error: 'Failed to bulk delete students'
      });
    }
  },
