import React, { useState, useCallback } from 'react';
import { Card, Form, Button, Alert, Row, Col } from 'react-bootstrap';
import { PersonPlus, InfoCircle } from 'react-bootstrap-icons';
import studentService from '../../services/studentService';

const StudentForm = ({ onStudentAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: ''
    // Removed resumeLink field
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [validationErrors, setValidationErrors] = useState({});

  const validateField = (name, value) => {
    switch (name) {
      case 'name':
        if (!value.trim()) return 'Name is required';
        if (value.trim().length < 2) return 'Name must be at least 2 characters';
        if (!/^[a-zA-Z\s'-]+$/.test(value.trim())) return 'Name can only contain letters, spaces, hyphens, and apostrophes';
        return '';
      
      case 'email':
        if (!value.trim()) return 'Email is required';
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value.trim())) return 'Please enter a valid email address';
        return '';
      
      case 'phone':
        if (!value.trim()) return 'Phone number is required';
        if (!/^[\+]?[1-9][\d]{0,15}$/.test(value.trim().replace(/[\s\-\(\)]/g, ''))) {
          return 'Please enter a valid phone number';
        }
        return '';
      
      default:
        return '';
    }
  };

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    // Clear general error
    if (error) setError('');

    // Validate field and update validation errors
    const fieldError = validateField(name, value);
    setValidationErrors(prev => ({
      ...prev,
      [name]: fieldError
    }));
  }, [error]);

  const validateForm = () => {
    const errors = {};
    Object.keys(formData).forEach(field => {
      const error = validateField(field, formData[field]);
      if (error) errors[field] = error;
    });
    
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      setError('Please fix the validation errors below');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const result = await studentService.addStudent({
        name: formData.name.trim(),
        email: formData.email.trim().toLowerCase(),
        phone: formData.phone.trim()
        // No resumeLink sent
      });

      // Reset form on success
      setFormData({ name: '', email: '', phone: '' });
      setValidationErrors({});
      
      // Show success message
      if (onStudentAdded) {
        onStudentAdded(result);
      }
    } catch (err) {
      console.error('Error adding student:', err);
      setError(err.response?.data?.error || 'Failed to add student. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [formData, onStudentAdded]);

  const resetForm = () => {
    setFormData({ name: '', email: '', phone: '' });
    setValidationErrors({});
    setError('');
  };

  return (
    <Card className="shadow-sm rounded border-0">
      <Card.Header className="d-flex align-items-center bg-primary text-white">
        <PersonPlus size={24} className="me-2" />
        <h5 className="mb-0">Add New Student</h5>
      </Card.Header>
      <Card.Body className="p-4">
        {error && (
          <Alert variant="danger" onClose={() => setError('')} dismissible className="mb-3">
            <i className="bi bi-exclamation-triangle me-2"></i>
            {error}
          </Alert>
        )}

        <Alert variant="info" className="mb-4 border-0" style={{ backgroundColor: '#e7f3ff' }}>
          <div className="d-flex align-items-start">
            <InfoCircle className="text-info me-2 mt-1" size={16} />
            <div>
              <strong className="text-info">Resume Collection Process</strong>
              <p className="mb-0 small text-muted mt-1">
                Students will upload their resume when they receive and use their interview invitation link. 
                This ensures you always get their most current resume.
              </p>
            </div>
          </div>
        </Alert>

        <Form onSubmit={handleSubmit} noValidate>
          <Row>
            <Col md={6}>
              <Form.Floating className="mb-3">
                <Form.Control
                  id="studentName"
                  name="name"
                  type="text"
                  placeholder="Enter student name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  maxLength={100}
                  autoComplete="name"
                  aria-label="Student name"
                  isInvalid={!!validationErrors.name}
                  className={validationErrors.name ? 'is-invalid' : formData.name ? 'is-valid' : ''}
                />
                <Form.Label htmlFor="studentName">Full Name *</Form.Label>
                {validationErrors.name && (
                  <Form.Control.Feedback type="invalid">
                    {validationErrors.name}
                  </Form.Control.Feedback>
                )}
              </Form.Floating>
            </Col>

            <Col md={6}>
              <Form.Floating className="mb-3">
                <Form.Control
                  id="studentEmail"
                  name="email"
                  type="email"
                  placeholder="Enter email address"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  maxLength={100}
                  autoComplete="email"
                  aria-label="Student email"
                  isInvalid={!!validationErrors.email}
                  className={validationErrors.email ? 'is-invalid' : formData.email ? 'is-valid' : ''}
                />
                <Form.Label htmlFor="studentEmail">Email Address *</Form.Label>
                {validationErrors.email && (
                  <Form.Control.Feedback type="invalid">
                    {validationErrors.email}
                  </Form.Control.Feedback>
                )}
              </Form.Floating>
            </Col>
          </Row>

          <Form.Floating className="mb-4">
            <Form.Control
              id="studentPhone"
              name="phone"
              type="tel"
              placeholder="Enter phone number"
              value={formData.phone}
              onChange={handleChange}
              required
              maxLength={20}
              autoComplete="tel"
              aria-label="Student phone"
              isInvalid={!!validationErrors.phone}
              className={validationErrors.phone ? 'is-invalid' : formData.phone ? 'is-valid' : ''}
            />
            <Form.Label htmlFor="studentPhone">Phone Number *</Form.Label>
            <Form.Text className="text-muted">
              📞 Include country code (e.g., +1 234 567 8900)
            </Form.Text>
            {validationErrors.phone && (
              <Form.Control.Feedback type="invalid">
                {validationErrors.phone}
              </Form.Control.Feedback>
            )}
          </Form.Floating>

          <div className="d-flex gap-2">
            <Button
              type="submit"
              variant="primary"
              disabled={loading || Object.keys(validationErrors).some(key => validationErrors[key])}
              aria-label="Add Student"
              className="flex-grow-1 d-flex align-items-center justify-content-center gap-2 fw-bold"
              size="lg"
            >
              {loading ? (
                <>
                  <span
                    className="spinner-border spinner-border-sm"
                    role="status"
                    aria-hidden="true"
                  />
                  Adding Student...
                </>
              ) : (
                <>
                  <PersonPlus size={18} />
                  Add Student
                </>
              )}
            </Button>

            <Button
              type="button"
              variant="outline-secondary"
              onClick={resetForm}
              disabled={loading}
              aria-label="Reset Form"
              size="lg"
            >
              <i className="bi bi-arrow-clockwise"></i>
            </Button>
          </div>
        </Form>

        {/* Form Status Indicators */}
        <div className="mt-3 d-flex align-items-center justify-content-between">
          <div className="d-flex gap-3">
            <small className={`d-flex align-items-center ${formData.name && !validationErrors.name ? 'text-success' : 'text-muted'}`}>
              <i className={`bi ${formData.name && !validationErrors.name ? 'bi-check-circle-fill' : 'bi-circle'} me-1`}></i>
              Name
            </small>
            <small className={`d-flex align-items-center ${formData.email && !validationErrors.email ? 'text-success' : 'text-muted'}`}>
              <i className={`bi ${formData.email && !validationErrors.email ? 'bi-check-circle-fill' : 'bi-circle'} me-1`}></i>
              Email
            </small>
            <small className={`d-flex align-items-center ${formData.phone && !validationErrors.phone ? 'text-success' : 'text-muted'}`}>
              <i className={`bi ${formData.phone && !validationErrors.phone ? 'bi-check-circle-fill' : 'bi-circle'} me-1`}></i>
              Phone
            </small>
          </div>
          <small className="text-muted">
            * Required fields
          </small>
        </div>
      </Card.Body>
    </Card>
  );
};

export default StudentForm;
