import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Alert, Spinner, Form, Modal } from 'react-bootstrap';
import { toast } from 'react-toastify';
import slotService from '../../services/slotService';
import studentService from '../../services/studentService';

const SlotSelection = () => {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const token = searchParams.get('token');
    
    const [availableSlots, setAvailableSlots] = useState([]);
    const [loading, setLoading] = useState(true);
    const [booking, setBooking] = useState(false);
    const [selectedSlot, setSelectedSlot] = useState(null);
    
    // Resume upload states
    const [showResumeModal, setShowResumeModal] = useState(false);
    const [resumeFile, setResumeFile] = useState(null);
    const [resumeUploading, setResumeUploading] = useState(false);
    const [currentResumeLink, setCurrentResumeLink] = useState('');
    const [studentInfo, setStudentInfo] = useState(null);

    useEffect(() => {
        if (!token) {
            navigate('/error');
            return;
        }
        loadData();
    }, [token, navigate]);

    const loadData = async () => {
        try {
            const [slots, student] = await Promise.all([
                slotService.getAvailableSlots(),
                studentService.getStudentByToken(token)
            ]);
            setAvailableSlots(slots);
            setStudentInfo(student);
            setCurrentResumeLink(student.resumeLink || '');
        } catch (error) {
            console.error('Error loading data:', error);
            toast.error('Failed to load data');
        } finally {
            setLoading(false);
        }
    };

    const handleResumeFileSelect = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        try {
            studentService.validateResumeFile(file);
            setResumeFile(file);
        } catch (error) {
            toast.error(error.message);
            event.target.value = '';
        }
    };

    const handleResumeUpload = async () => {
        if (!resumeFile) {
            toast.error('Please select a resume file first');
            return;
        }

        setResumeUploading(true);
        try {
            const result = await studentService.updateResume(token, resumeFile);
            setCurrentResumeLink(result.resumeLink);
            toast.success('Resume updated successfully!');
            setShowResumeModal(false);
            setResumeFile(null);
            document.getElementById('resume-upload').value = '';
        } catch (error) {
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Failed to update resume: ' + errorMessage);
        } finally {
            setResumeUploading(false);
        }
    };

    const handleSlotSelection = async (slotId) => {
        setSelectedSlot(slotId);
        setBooking(true);
        try {
            const result = await slotService.bookSlot(slotId, token);
            toast.success('Interview slot booked successfully!');
            navigate('/student/booking-confirmation', { 
                state: { 
                    slotDateTime: result.slotDateTime,
                    meetingLink: result.meetingLink 
                } 
            });
        } catch (error) {
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Failed to book slot: ' + errorMessage);
            setSelectedSlot(null);
        } finally {
            setBooking(false);
        }
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', {
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    const formatTime = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleTimeString('en-US', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: true
        });
    };

    const isToday = (dateString) => {
        const today = new Date();
        const slotDate = new Date(dateString);
        return today.toDateString() === slotDate.toDateString();
    };

    const isTomorrow = (dateString) => {
        const tomorrow = new Date();
        tomorrow.setDate(tomorrow.getDate() + 1);
        const slotDate = new Date(dateString);
        return tomorrow.toDateString() === slotDate.toDateString();
    };

    if (loading) {
        return (
            <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '70vh' }}>
                <div className="text-center">
                    <Spinner animation="border" role="status" size="lg" className="mb-3" style={{ color: '#007bff' }}>
                        <span className="visually-hidden">Loading...</span>
                    </Spinner>
                    <h5 className="text-muted">Loading available slots...</h5>
                </div>
            </Container>
        );
    }

    return (
        <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh', paddingTop: '2rem', paddingBottom: '2rem' }}>
            <Container>
                <Row className="justify-content-center">
                    <Col lg={10} xl={8}>
                        {/* Header Section */}
                        <div className="text-center mb-4">
                            <div className="mb-3">
                                <i className="bi bi-calendar-check" style={{ fontSize: '3rem', color: '#007bff' }}></i>
                            </div>
                            <h2 className="fw-bold text-dark mb-2">Select Your Interview Slot</h2>
                            <p className="text-muted fs-5">Choose your preferred time slot from the available options below</p>
                            
                            {/* Student Info & Resume Section */}
                            {studentInfo && (
                                <Card className="mb-4 border-0 shadow-sm" style={{ borderRadius: '10px' }}>
                                    <Card.Body className="py-3">
                                        <Row className="align-items-center">
                                            <Col md={8}>
                                                <div className="text-start">
                                                    <h6 className="mb-1">Welcome, {studentInfo.name}</h6>
                                                    <small className="text-muted">
                                                        <i className="bi bi-envelope me-2"></i>{studentInfo.email}
                                                    </small>
                                                    {currentResumeLink && (
                                                        <div className="mt-1">
                                                            <small className="text-success">
                                                                <i className="bi bi-file-earmark-text me-2"></i>
                                                                Resume uploaded
                                                            </small>
                                                        </div>
                                                    )}
                                                </div>
                                            </Col>
                                            <Col md={4} className="text-end">
                                                <Button
                                                    variant={currentResumeLink ? "outline-primary" : "primary"}
                                                    size="sm"
                                                    onClick={() => setShowResumeModal(true)}
                                                >
                                                    <i className="bi bi-upload me-2"></i>
                                                    {currentResumeLink ? 'Update Resume' : 'Upload Resume'}
                                                </Button>
                                            </Col>
                                        </Row>
                                    </Card.Body>
                                </Card>
                            )}

                            <div className="mb-3">
                                <Button
                                    variant="info"
                                    className="px-3 py-2 border-0"
                                    disabled
                                >
                                    <i className="bi bi-clock me-2"></i>
                                    {availableSlots.length} Slots Available
                                </Button>
                            </div>
                        </div>

                        {/* Main Content Card */}
                        <Card className="shadow-lg border-0" style={{ borderRadius: '15px' }}>
                            <Card.Body className="p-4">
                                {availableSlots.length === 0 ? (
                                    <Alert variant="warning" className="text-center border-0 shadow-sm" style={{ borderRadius: '10px' }}>
                                        <div className="mb-3">
                                            <i className="bi bi-exclamation-triangle" style={{ fontSize: '2.5rem', color: '#856404' }}></i>
                                        </div>
                                        <Alert.Heading className="h4">No Available Slots</Alert.Heading>
                                        <p className="mb-3 fs-6">There are currently no available interview slots.</p>
                                        <hr />
                                        <p className="mb-0">
                                            <i className="bi bi-telephone me-2"></i>
                                            Please check back later or contact HR for assistance.
                                        </p>
                                    </Alert>
                                ) : (
                                    <Row className="g-4">
                                        {availableSlots.map(slot => (
                                            <Col md={6} lg={4} key={slot.Id}>
                                                <Card 
                                                    className={`h-100 slot-card ${selectedSlot === slot.Id ? 'border-primary shadow-lg' : 'border-light shadow-sm'}`}
                                                    style={{ 
                                                        cursor: 'pointer',
                                                        borderRadius: '12px',
                                                        transition: 'all 0.3s ease',
                                                        transform: selectedSlot === slot.Id ? 'translateY(-5px)' : 'none'
                                                    }}
                                                    onClick={() => !booking && setSelectedSlot(slot.Id)}
                                                >
                                                    <Card.Body className="text-center p-4">
                                                        {/* Date Badge */}
                                                        <div className="mb-3">
                                                            {isToday(slot.SlotDateTime) && (
                                                                <span className="badge bg-success mb-2">Today</span>
                                                            )}
                                                            {isTomorrow(slot.SlotDateTime) && (
                                                                <span className="badge bg-info mb-2">Tomorrow</span>
                                                            )}
                                                        </div>

                                                        {/* Calendar Icon */}
                                                        <div className="mb-3">
                                                            <i className="bi bi-calendar-date" 
                                                               style={{ 
                                                                   fontSize: '2rem', 
                                                                   color: selectedSlot === slot.Id ? '#007bff' : '#6c757d' 
                                                               }}></i>
                                                        </div>

                                                        {/* Date */}
                                                        <h5 className="fw-bold text-dark mb-2">
                                                            {formatDate(slot.SlotDateTime)}
                                                        </h5>

                                                        {/* Time */}
                                                        <p className="text-muted mb-3 fs-5">
                                                            <i className="bi bi-clock me-2"></i>
                                                            {formatTime(slot.SlotDateTime)}
                                                        </p>

                                                        {/* Select Button */}
                                                        <Button
                                                            variant={selectedSlot === slot.Id ? 'primary' : 'outline-primary'}
                                                            size="lg"
                                                            className="w-100 fw-bold"
                                                            style={{ borderRadius: '8px', padding: '12px' }}
                                                            onClick={(e) => {
                                                                e.stopPropagation();
                                                                handleSlotSelection(slot.Id);
                                                            }}
                                                            disabled={booking}
                                                        >
                                                            {booking && selectedSlot === slot.Id ? (
                                                                <>
                                                                    <Spinner
                                                                        as="span"
                                                                        animation="border"
                                                                        size="sm"
                                                                        role="status"
                                                                        aria-hidden="true"
                                                                        className="me-2"
                                                                    />
                                                                    Booking...
                                                                </>
                                                            ) : (
                                                                <>
                                                                    <i className="bi bi-check-circle me-2"></i>
                                                                    Select This Slot
                                                                </>
                                                            )}
                                                        </Button>
                                                    </Card.Body>
                                                </Card>
                                            </Col>
                                        ))}
                                    </Row>
                                )}
                            </Card.Body>
                        </Card>

                        {/* Help Section */}
                        <Card className="mt-4 border-0 shadow-sm" style={{ borderRadius: '10px', backgroundColor: '#f8f9fa' }}>
                            <Card.Body className="text-center py-3">
                                <small className="text-muted">
                                    <i className="bi bi-info-circle me-2"></i>
                                    Need help? Contact us at <strong>hr@company.com</strong> or call <strong>(555) 123-4567</strong>
                                </small>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>

            {/* Resume Upload Modal */}
            <Modal show={showResumeModal} onHide={() => setShowResumeModal(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>
                        <i className="bi bi-file-earmark-arrow-up me-2"></i>
                        Update Resume
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {currentResumeLink && (
                        <Alert variant="info" className="mb-3">
                            <i className="bi bi-info-circle me-2"></i>
                            You already have a resume uploaded. Uploading a new one will replace the existing file.
                        </Alert>
                    )}

                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>Select Resume File</Form.Label>
                            <Form.Control
                                id="resume-upload"
                                type="file"
                                accept=".pdf,.doc,.docx,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                                onChange={handleResumeFileSelect}
                            />
                            <Form.Text className="text-muted">
                                Supported formats: PDF, DOC, DOCX (Max size: 5MB)
                            </Form.Text>
                        </Form.Group>

                        {resumeFile && (
                            <Alert variant="success" className="mb-3">
                                <strong>Selected file:</strong> {resumeFile.name}
                                <br />
                                <small>Size: {studentService.formatFileSize(resumeFile.size)}</small>
                            </Alert>
                        )}
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowResumeModal(false)} disabled={resumeUploading}>
                        Cancel
                    </Button>
                    <Button 
                        variant="primary" 
                        onClick={handleResumeUpload}
                        disabled={!resumeFile || resumeUploading}
                    >
                        {resumeUploading ? (
                            <>
                                <Spinner
                                    as="span"
                                    animation="border"
                                    size="sm"
                                    role="status"
                                    aria-hidden="true"
                                    className="me-2"
                                />
                                Uploading...
                            </>
                        ) : (
                            <>
                                <i className="bi bi-upload me-2"></i>
                                Upload Resume
                            </>
                        )}
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default SlotSelection;
