import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

// Components
import Navbar from './components/common/Navbar';
import AdminPage from './pages/AdminPage';
import StudentPage from './pages/StudentPage';
import SlotSelection from './components/student/SlotSelection';
import BookingConfirmation from './components/student/BookingConfirmation';
import ErrorPage from './pages/ErrorPage';

// Auth Components
import Login from './components/auth/Login';
import ProtectedRoute from './components/auth/ProtectedRoute';

// Services
import authService from './services/authService';

// Styles
import './styles/index.css';

// Layout component to handle navbar conditionally
const Layout = ({ children }) => {
    const currentPath = window.location.pathname;
    
    // Paths where navbar should be hidden
    const hideNavbarPaths = [
        '/login', 
        '/student', 
        '/student/select-slot', 
        '/student/booking-confirmation',
        '/error'
    ];
    
    const shouldHideNavbar = hideNavbarPaths.some(path => currentPath.startsWith(path));
    const isAuthenticated = authService.isAuthenticated() && !authService.isTokenExpired();
    
    return (
        <>
            {!shouldHideNavbar && isAuthenticated && <Navbar />}
            <main>
                {children}
            </main>
        </>
    );
};

function App() {
    return (
        <Router
            future={{
                v7_startTransition: true,
                v7_relativeSplatPath: true
            }}
        >
            <div className="App">
                <Layout>
                    <Routes>
                        {/* Public Authentication Route */}
                        <Route path="/login" element={<Login />} />
                        
                        {/* Public Student Routes (accessed via email invitation) */}
                        <Route path="/student" element={<StudentPage />} />
                        <Route path="/student/select-slot" element={<SlotSelection />} />
                        <Route path="/student/booking-confirmation" element={<BookingConfirmation />} />
                        
                        {/* Protected Admin Routes */}
                        <Route 
                            path="/admin" 
                            element={
                                <ProtectedRoute>
                                    <AdminPage />
                                </ProtectedRoute>
                            } 
                        />
                        
                        {/* Default redirect */}
                        <Route path="/" element={<Navigate to="/admin" replace />} />
                        
                        {/* Error page */}
                        <Route path="/error" element={<ErrorPage />} />
                        
                        {/* Catch all */}
                        <Route path="*" element={<Navigate to="/admin" replace />} />
                    </Routes>
                </Layout>

                {/* Toast notifications */}
                <ToastContainer
                    position="top-right"
                    autoClose={5000}
                    hideProgressBar={false}
                    newestOnTop={false}
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover
                    theme="light"
                />
            </div>
        </Router>
    );
}

export default App;
