import React, { useState, useEffect, useCallback } from 'react';
import {
  Table,
  Badge,
  Button,
  Modal,
  Form,
  Card,
  Row,
  Col,
} from 'react-bootstrap';
import { CalendarCheck, Pencil, Trash, CameraVideo } from 'react-bootstrap-icons';
import { toast } from 'react-toastify';
import interviewService from '../../services/interviewService';

const InterviewList = ({ interviews: initialInterviews }) => {
  const [interviews, setInterviews] = useState(initialInterviews || []);
  const [showModal, setShowModal] = useState(false);
  const [selectedInterview, setSelectedInterview] = useState(null);
  const [newStatus, setNewStatus] = useState('');

  useEffect(() => {
    if (initialInterviews) {
      setInterviews(initialInterviews);
    }
  }, [initialInterviews]);

  const getStatusVariant = useCallback((status) => {
    const variants = {
      Invited: 'info',
      Scheduled: 'primary',
      Completed: 'success',
      Cancelled: 'danger',
      'No-Show': 'warning',
    };
    return variants[status] || 'secondary';
  }, []);

  const handleStatusUpdate = useCallback((interview) => {
    setSelectedInterview(interview);
    setNewStatus(interview.Status);
    setShowModal(true);
  }, []);

  const submitStatusUpdate = useCallback(async () => {
    if (!selectedInterview) return;
    try {
      await interviewService.updateStatus(selectedInterview.Id, newStatus);
      setInterviews((prev) =>
        prev.map((i) =>
          i.Id === selectedInterview.Id ? { ...i, Status: newStatus } : i
        )
      );
      toast.success('Interview status updated successfully');
      setShowModal(false);
    } catch (error) {
      toast.error('Failed to update interview status');
    }
  }, [newStatus, selectedInterview]);

  const handleDeleteInterview = useCallback(
    async (id) => {
      if (window.confirm('Are you sure you want to delete this interview?')) {
        try {
          await interviewService.deleteInterview(id);
          setInterviews((prev) => prev.filter((i) => i.Id !== id));
          toast.success('Interview deleted');
        } catch {
          toast.error('Failed to delete interview');
        }
      }
    },
    []
  );

  return (
    <>
      <Row className="mb-3 align-items-center">
        <Col xs="auto" className="d-flex align-items-center text-primary fs-5 fw-bold">
          <CalendarCheck className="me-2" /> Scheduled Interviews
        </Col>
        <Col xs="auto">
          <Badge bg="info" pill>
            {interviews.length} Total
          </Badge>
        </Col>
      </Row>

      <Card className="shadow-sm rounded">
        <Card.Body className="p-0">
          <div className="table-responsive">
            <Table
              striped
              bordered
              hover
              className="mb-0 align-middle text-nowrap"
              responsive
            >
              <thead className="table-dark">
                <tr>
                  <th>Student</th>
                  <th>Email</th>
                  <th>Slot Date & Time</th>
                  <th>Interviewer</th>
                  <th>Status</th>
                  <th>Meeting Link</th>
                  <th style={{ width: '130px' }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {interviews.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="text-center text-muted py-4">
                      No interviews scheduled yet
                    </td>
                  </tr>
                ) : (
                  interviews.map((interview) => (
                    <tr key={interview.Id}>
                      <td>{interview.StudentName}</td>
                      <td>{interview.StudentEmail}</td>
                      <td>
                        {interview.SlotDateTime
                          ? new Date(interview.SlotDateTime).toLocaleString()
                          : '-'}
                      </td>
                      <td>{interview.Interviewer || '-'}</td>
                      <td>
                        <Badge bg={getStatusVariant(interview.Status)} pill>
                          {interview.Status}
                        </Badge>
                      </td>
                      <td>
                        {interview.MeetingLink ? (
                          <Button
                            as="a"
                            href={interview.MeetingLink}
                            target="_blank"
                            rel="noopener noreferrer"
                            size="sm"
                            variant="outline-primary"
                            aria-label={`Join meeting for ${interview.StudentName}`}
                            className="d-flex align-items-center gap-1"
                          >
                            <CameraVideo />
                            Join
                          </Button>
                        ) : (
                          '-'
                        )}
                      </td>
                      <td>
                        <Button
                          size="sm"
                          variant="outline-secondary"
                          className="me-2"
                          onClick={() => handleStatusUpdate(interview)}
                          aria-label={`Update interview status for ${interview.StudentName}`}
                        >
                          <Pencil /> Update
                        </Button>
                        <Button
                          size="sm"
                          variant="outline-danger"
                          onClick={() => handleDeleteInterview(interview.Id)}
                          aria-label={`Delete interview for ${interview.StudentName}`}
                        >
                          <Trash />
                        </Button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </Table>
          </div>
        </Card.Body>
      </Card>

      {/* Status Update Modal */}
      <Modal
        show={showModal}
        onHide={() => setShowModal(false)}
        centered
        backdrop="static"
        keyboard={false}
        aria-labelledby="update-status-modal"
      >
        <Modal.Header closeButton>
          <Modal.Title id="update-status-modal">Update Interview Status</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Floating>
              <Form.Select
                id="interviewStatus"
                value={newStatus}
                onChange={(e) => setNewStatus(e.target.value)}
                aria-label="Select interview status"
              >
                <option value="Invited">Invited</option>
                <option value="Scheduled">Scheduled</option>
                <option value="Completed">Completed</option>
                <option value="Cancelled">Cancelled</option>
                <option value="No-Show">No-Show</option>
              </Form.Select>
              <Form.Label htmlFor="interviewStatus">Interview Status</Form.Label>
            </Form.Floating>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={submitStatusUpdate}>
            Update Status
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default InterviewList;
