import api from './api';

const slotService = {
    getSlots: async () => {
        try {
            const response = await api.get('/slots');
            return response.data;
        } catch (error) {
            console.error('Failed to get slots:', error);
            throw error;
        }
    },

    getAvailableSlots: async () => {
        try {
            const response = await api.get('/slots/available');
            console.log('ðŸ“… Available slots loaded:', response.data.length);
            return response.data;
        } catch (error) {
            console.error('Failed to get available slots:', error);
            throw error;
        }
    },

    createSlot: async (slotData) => {
        try {
            if (!slotData || !slotData.slotDateTime) {
                throw new Error('Slot date and time are required');
            }

            const response = await api.post('/slots', slotData);
            console.log('âœ… Slot created successfully:', response.data);
            return response.data;
        } catch (error) {
            console.error('Failed to create slot:', error);
            throw error;
        }
    },

    bookSlot: async (slotId, token) => {
        try {
            console.log('ðŸ“… Attempting to book slot:', { slotId, token: token ? `${token.substring(0, 10)}...` : 'missing' });
            
            // Validate input parameters
            if (!slotId) {
                throw new Error('Slot ID is required');
            }
            
            if (!token) {
                throw new Error('Authentication token is required');
            }

            // Ensure slotId is a number and token is a clean string
            const bookingData = {
                slotId: parseInt(slotId), 
                token: token.trim()
            };

            // Validate slotId is a valid number
            if (isNaN(bookingData.slotId)) {
                throw new Error('Invalid slot ID format');
            }

            const response = await api.post('/slots/book', bookingData);
            
            console.log('âœ… Slot booked successfully:', {
                slotId: bookingData.slotId,
                studentName: response.data.studentName,
                slotDateTime: response.data.slotDateTime
            });
            
            return response.data;
        } catch (error) {
            console.error('âŒ Failed to book slot:', error);
            
            // Enhanced error handling with specific messages
            if (error.response) {
                // Server responded with error status
                const serverError = error.response.data?.error || 'Server error occurred';
                const statusCode = error.response.status;
                
                // Handle specific status codes
                switch (statusCode) {
                    case 400:
                        throw new Error(serverError);
                    case 401:
                        throw new Error('Authentication failed. Please check your invitation link.');
                    case 404:
                        throw new Error('Slot not found or invitation token is invalid.');
                    case 409:
                        throw new Error('Slot is no longer available. Please select another slot.');
                    case 500:
                        throw new Error('Server error. Please try again later.');
                    default:
                        throw new Error(serverError);
                }
            } else if (error.request) {
                // Request timeout or network error
                if (error.code === 'ECONNABORTED') {
                    throw new Error('Request timeout. Please check your connection and try again.');
                }
                throw new Error('Network error. Please check your internet connection.');
            } else {
                // Other errors (validation, etc.)
                throw new Error(error.message || 'An unexpected error occurred while booking the slot.');
            }
        }
    },

    deleteSlot: async (id) => {
        try {
            if (!id) {
                throw new Error('Slot ID is required');
            }

            const slotId = parseInt(id);
            if (isNaN(slotId)) {
                throw new Error('Invalid slot ID format');
            }

            const response = await api.delete(`/slots/${slotId}`);
            console.log('ðŸ—‘ï¸ Slot deleted successfully:', slotId);
            return response.data;
        } catch (error) {
            console.error('Failed to delete slot:', error);
            
            if (error.response?.status === 404) {
                throw new Error('Slot not found');
            } else if (error.response?.status === 400) {
                throw new Error(error.response.data?.error || 'Cannot delete this slot');
            }
            
            throw error;
        }
    },

    // Helper method to validate slot data
    validateSlotData: (slotData) => {
        if (!slotData) {
            throw new Error('Slot data is required');
        }

        if (!slotData.slotDateTime) {
            throw new Error('Slot date and time are required');
        }

        const slotDate = new Date(slotData.slotDateTime);
        const now = new Date();

        if (isNaN(slotDate.getTime())) {
            throw new Error('Invalid date format');
        }

        if (slotDate <= now) {
            throw new Error('Slot date must be in the future');
        }

        return true;
    },

    // Helper method to format slot date for display
    formatSlotDateTime: (dateTimeString) => {
        try {
            const date = new Date(dateTimeString);
            return {
                date: date.toLocaleDateString('en-US', {
                    weekday: 'long',
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric'
                }),
                time: date.toLocaleTimeString('en-US', {
                    hour: '2-digit',
                    minute: '2-digit',
                    hour12: true
                }),
                full: date.toLocaleString('en-US', {
                    weekday: 'long',
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric',
                    hour: '2-digit',
                    minute: '2-digit',
                    hour12: true
                }),
                isToday: date.toDateString() === new Date().toDateString(),
                isTomorrow: date.toDateString() === new Date(Date.now() + 86400000).toDateString()
            };
        } catch (error) {
            console.error('Error formatting date:', error);
            return {
                date: 'Invalid Date',
                time: 'Invalid Time',
                full: 'Invalid Date/Time',
                isToday: false,
                isTomorrow: false
            };
        }
    },

    // Helper method to check if a slot is bookable
    isSlotBookable: (slotDateTime) => {
        const slotDate = new Date(slotDateTime);
        const now = new Date();
        const bufferMinutes = 30; // 30-minute buffer
        
        return slotDate > new Date(now.getTime() + (bufferMinutes * 60 * 1000));
    },

    // Method to get slot statistics
    getSlotStats: async () => {
        try {
            const [allSlots, availableSlots] = await Promise.all([
                slotService.getSlots(),
                slotService.getAvailableSlots()
            ]);

            const now = new Date();
            const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
            const tomorrow = new Date(today.getTime() + 86400000);

            const stats = {
                total: allSlots.length,
                available: availableSlots.length,
                booked: allSlots.filter(slot => slot.IsBooked).length,
                today: availableSlots.filter(slot => {
                    const slotDate = new Date(slot.SlotDateTime);
                    return slotDate.toDateString() === today.toDateString();
                }).length,
                tomorrow: availableSlots.filter(slot => {
                    const slotDate = new Date(slot.SlotDateTime);
                    return slotDate.toDateString() === tomorrow.toDateString();
                }).length,
                upcoming: availableSlots.filter(slot => {
                    const slotDate = new Date(slot.SlotDateTime);
                    return slotDate > tomorrow;
                }).length
            };

            console.log('ðŸ“Š Slot statistics:', stats);
            return stats;
        } catch (error) {
            console.error('Failed to get slot statistics:', error);
            throw error;
        }
    }
};

export default slotService;
