import React, { useState, useEffect, useCallback } from "react";
import {
  Container,
  Row,
  Col,
  Tab,
  Tabs,
  Button,
  Card,
} from "react-bootstrap";
import StudentForm from "./StudentForm";
import ExcelUpload from "./ExcelUpload";
import SlotCreator from "./SlotCreator";
import InterviewList from "./InterviewList";
import { toast } from "react-toastify";
import studentService from "../../services/studentService";
import slotService from "../../services/slotService";
import interviewService from "../../services/interviewService";
import "bootstrap-icons/font/bootstrap-icons.css"; // Bootstrap Icons

const AdminDashboard = () => {
  const [students, setStudents] = useState([]);
  const [slots, setSlots] = useState([]);
  const [interviews, setInterviews] = useState([]);
  const [selectedStudents, setSelectedStudents] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = useCallback(async () => {
    try {
      const [studentsData, slotsData, interviewsData] = await Promise.all([
        studentService.getStudents(),
        slotService.getSlots(),
        interviewService.getInterviews(),
      ]);
      setStudents(studentsData);
      setSlots(slotsData);
      setInterviews(interviewsData);
    } catch (error) {
      toast.error("Failed to load data");
      console.error("Load data error:", error);
    }
  }, []);

  const handleStudentAdded = () => {
    loadData();
    toast.success("Student added successfully");
  };

  const handleExcelUploaded = (result) => {
    loadData();
    toast.success(
      `Excel processed: ${result.successful} successful, ${result.failed} failed`
    );
  };

  const handleSlotCreated = () => {
    loadData();
    toast.success("Interview slot created successfully");
  };

  const handleSendInvitations = async () => {
    if (selectedStudents.length === 0) {
      toast.warning("Please select students to send invitations");
      return;
    }
    try {
      await studentService.sendInvitations(selectedStudents);
      toast.success("Invitations sent successfully");
      setSelectedStudents([]);
    } catch {
      toast.error("Failed to send invitations");
    }
  };

  return (
    <Container fluid className="py-4">
      {/* Header */}
      <Row className="mb-4">
        <Col>
          <h2 className="fw-bold text-primary d-flex align-items-center">
            <i className="bi bi-speedometer2 me-2"></i>
            Interview Scheduler - Admin Dashboard
          </h2>
        </Col>
      </Row>

      {/* Tabs */}
      <Tabs defaultActiveKey="students" className="mb-4" fill>
        {/* Student Management */}
        <Tab
          eventKey="students"
          title={<><i className="bi bi-people me-2"></i>Student Management</>}
        >
          <Row>
            <Col xs={12} md={6} className="mb-4">
              <Card className="shadow-sm h-100">
                <Card.Header>
                  <i className="bi bi-person-plus me-2"></i>Add Student
                </Card.Header>
                <Card.Body>
                  <StudentForm onStudentAdded={handleStudentAdded} />
                </Card.Body>
              </Card>
            </Col>
            <Col xs={12} md={6} className="mb-4">
              <Card className="shadow-sm h-100">
                <Card.Header>
                  <i className="bi bi-file-earmark-excel me-2"></i>Upload Excel
                </Card.Header>
                <Card.Body>
                  <ExcelUpload onExcelUploaded={handleExcelUploaded} />
                </Card.Body>
              </Card>
            </Col>
          </Row>

          <Card className="shadow-sm">
            <Card.Header className="d-flex justify-content-between align-items-center">
              <h5 className="mb-0">
                <i className="bi bi-list-ul me-2"></i>Students List
              </h5>
              <Button
                variant="primary"
                onClick={handleSendInvitations}
                disabled={selectedStudents.length === 0}
              >
                <i className="bi bi-envelope-paper me-2"></i>
                Send Invitations ({selectedStudents.length})
              </Button>
            </Card.Header>
            <Card.Body className="p-0">
              <div className="table-responsive">
                <table className="table table-hover align-middle mb-0">
                  <thead className="table-light sticky-top">
                    <tr>
                      <th>
                        <input
                          type="checkbox"
                          checked={
                            selectedStudents.length === students.length &&
                            students.length > 0
                          }
                          onChange={(e) =>
                            setSelectedStudents(
                              e.target.checked ? students.map((s) => s.Id) : []
                            )
                          }
                        />
                      </th>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Phone</th>
                      <th>Resume</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {students.map((student) => (
                      <tr key={student.Id}>
                        <td>
                          <input
                            type="checkbox"
                            checked={selectedStudents.includes(student.Id)}
                            onChange={(e) =>
                              setSelectedStudents(
                                e.target.checked
                                  ? [...selectedStudents, student.Id]
                                  : selectedStudents.filter(
                                      (id) => id !== student.Id
                                    )
                              )
                            }
                          />
                        </td>
                        <td className="fw-semibold">{student.Name}</td>
                        <td>{student.Email}</td>
                        <td>{student.Phone}</td>
                        <td>
                          {student.ResumeLink && (
                            <a
                              href={student.ResumeLink}
                              target="_blank"
                              rel="noopener noreferrer"
                            >
                              <i className="bi bi-file-earmark-text me-1 text-primary"></i>
                              Resume
                            </a>
                          )}
                        </td>
                        <td>
                          <Button
                            variant="danger"
                            size="sm"
                            className="d-inline-flex align-items-center"
                            onClick={async () => {
                              if (
                                window.confirm(
                                  "Are you sure you want to delete this student?"
                                )
                              ) {
                                try {
                                  await studentService.deleteStudent(student.Id);
                                  toast.success("Student deleted");
                                  loadData();
                                } catch (error) {
                                  toast.error(
                                    error.response?.data?.error ||
                                      "Failed to delete student"
                                  );
                                }
                              }
                            }}
                          >
                            <i className="bi bi-trash me-1"></i> Delete
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </Card.Body>
          </Card>
        </Tab>

        {/* Slots */}
        <Tab
          eventKey="slots"
          title={<><i className="bi bi-calendar-event me-2"></i>Interview Slots</>}
        >
          <Row>
            <Col xs={12} md={4} className="mb-4">
              <Card className="shadow-sm h-100">
                <Card.Header>
                  <i className="bi bi-plus-circle me-2"></i>Create Slot
                </Card.Header>
                <Card.Body>
                  <SlotCreator onSlotCreated={handleSlotCreated} />
                </Card.Body>
              </Card>
            </Col>
            <Col xs={12} md={8}>
              <Card className="shadow-sm">
                <Card.Header>
                  <h5 className="mb-0">
                    <i className="bi bi-clock-history me-2"></i>Available Slots
                  </h5>
                </Card.Header>
                <Card.Body className="p-0">
                  <div className="table-responsive">
                    <table className="table table-hover align-middle mb-0">
                      <thead className="table-light sticky-top">
                        <tr>
                          <th>Date & Time</th>
                          <th>Status</th>
                          <th>Booked By</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {slots.map((slot) => (
                          <tr key={slot.Id}>
                            <td>{new Date(slot.SlotDateTime).toLocaleString()}</td>
                            <td>
                              <span
                                className={`badge ${
                                  slot.IsBooked ? "bg-danger" : "bg-success"
                                }`}
                              >
                                {slot.IsBooked ? "Booked" : "Available"}
                              </span>
                            </td>
                            <td>{slot.IsBooked ? slot.StudentName : "-"}</td>
                            <td>
                              <Button
                                variant="danger"
                                size="sm"
                                onClick={async () => {
                                  if (
                                    window.confirm(
                                      "Are you sure you want to delete this slot?"
                                    )
                                  ) {
                                    try {
                                      await slotService.deleteSlot(slot.Id);
                                      toast.success("Slot deleted");
                                      loadData();
                                    } catch {
                                      toast.error("Failed to delete slot");
                                    }
                                  }
                                }}
                              >
                                <i className="bi bi-trash"></i>
                              </Button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Tab>

        {/* Interviews */}
        <Tab
          eventKey="interviews"
          title={<><i className="bi bi-person-video3 me-2"></i>Scheduled Interviews</>}
        >
          <InterviewList interviews={interviews} />
        </Tab>
      </Tabs>
    </Container>
  );
};

export default React.memo(AdminDashboard);
