import React, { useState, useCallback } from 'react';
import { Card, Form, Button, Alert, Table, Badge } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { PlusCircle, CalendarPlus, Trash } from 'react-bootstrap-icons';
import slotService from '../../services/slotService';

export const SlotCreator = ({ onSlotCreated }) => {
  const [selectedDate, setSelectedDate] = useState(() => new Date());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = useCallback(
    async (e) => {
      e.preventDefault();
      setLoading(true);
      setError('');
      try {
        await slotService.createSlot({ slotDateTime: selectedDate });
        setSelectedDate(new Date());
        onSlotCreated();
      } catch (err) {
        setError(err.response?.data?.error || 'Failed to create slot');
      } finally {
        setLoading(false);
      }
    },
    [selectedDate, onSlotCreated]
  );

  return (
    <Card className="shadow-sm rounded mb-4">
      <Card.Header className="d-flex align-items-center bg-success text-white">
        <CalendarPlus size={24} className="me-2" />
        <h5 className="mb-0">Create Interview Slot</h5>
      </Card.Header>
      <Card.Body>
        {error && (
          <Alert variant="danger" onClose={() => setError('')} dismissible>
            {error}
          </Alert>
        )}

        <Form onSubmit={handleSubmit} noValidate>
          <Form.Group className="mb-3">
            <Form.Label className="fw-semibold" htmlFor="slot-date-picker">
              Select Date &amp; Time
            </Form.Label>
            <DatePicker
              id="slot-date-picker"
              selected={selectedDate}
              onChange={date => setSelectedDate(date || new Date())}
              showTimeSelect
              dateFormat="MMMM d, yyyy h:mm aa"
              minDate={new Date()}
              className="form-control rounded"
              placeholderText="Select date and time"
              autoComplete="off"
              aria-label="Select date and time for interview slot"
              required
            />
          </Form.Group>
          <Form.Text className="text-muted mb-3 d-block">
            Select a future date and time for the interview slot
          </Form.Text>
          <div className="d-grid">
            <Button
              type="submit"
              variant="success"
              disabled={loading}
              aria-label="Create slot"
              className="d-flex align-items-center justify-content-center gap-2 fs-5"
            >
              {loading ? (
                <>
                  <span
                    className="spinner-border spinner-border-sm"
                    role="status"
                    aria-hidden="true"
                  />
                  Creating...
                </>
              ) : (
                <>
                  <PlusCircle />
                  Create Slot
                </>
              )}
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};




export default SlotCreator;
