import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

// Components
import Navbar from './components/common/Navbar';
import AdminPage from './pages/AdminPage';
import StudentPage from './pages/StudentPage';
import SlotSelection from './components/student/SlotSelection';
import BookingConfirmation from './components/student/BookingConfirmation';
import ErrorPage from './pages/ErrorPage';

// Styles
import './styles/index.css';

function App() {
    return (
        <Router
            future={{
                v7_startTransition: true,
                v7_relativeSplatPath: true
            }}
        >
            <div className="App">
                <Navbar />
                
                <Routes>
                    {/* Admin Routes */}
                    <Route path="/admin" element={<AdminPage />} />
                    
                    {/* Student Routes */}
                    <Route path="/student" element={<StudentPage />} />
                    <Route path="/student/select-slot" element={<SlotSelection />} />
                    <Route path="/student/booking-confirmation" element={<BookingConfirmation />} />
                    
                    {/* Default Routes */}
                    <Route path="/" element={<Navigate to="/admin" replace />} />
                    <Route path="/error" element={<ErrorPage />} />
                    <Route path="*" element={<Navigate to="/error" replace />} />
                </Routes>

                <ToastContainer
                    position="top-right"
                    autoClose={5000}
                    hideProgressBar={false}
                    newestOnTop={false}
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover
                    theme="light"
                />
            </div>
        </Router>
    );
}

export default App;
