import React, { useState, useCallback } from 'react';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import { PersonPlus } from 'react-bootstrap-icons';
import studentService from '../../services/studentService';

const StudentForm = ({ onStudentAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    resumeLink: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = useCallback(
    (e) => {
      const { name, value } = e.target;
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
      if (error) setError('');
    },
    [error]
  );

  const handleSubmit = useCallback(
    async (e) => {
      e.preventDefault();
      setLoading(true);
      setError('');
      try {
        await studentService.addStudent(formData);
        setFormData({ name: '', email: '', phone: '', resumeLink: '' });
        onStudentAdded();
      } catch (err) {
        setError(err.response?.data?.error || 'Failed to add student');
      } finally {
        setLoading(false);
      }
    },
    [formData, onStudentAdded]
  );

  return (
    <Card className="shadow-sm rounded">
      <Card.Header className="d-flex align-items-center bg-primary text-white">
        <PersonPlus size={24} className="me-2" />
        <h5 className="mb-0">Add New Student</h5>
      </Card.Header>
      <Card.Body>
        {error && (
          <Alert variant="danger" onClose={() => setError('')} dismissible>
            {error}
          </Alert>
        )}
        <Form onSubmit={handleSubmit} noValidate>
          <Form.Floating className="mb-3">
            <Form.Control
              id="studentName"
              name="name"
              type="text"
              placeholder="Enter student name"
              value={formData.name}
              onChange={handleChange}
              required
              maxLength={100}
              autoComplete="name"
              aria-label="Student name"
            />
            <Form.Label htmlFor="studentName">Name *</Form.Label>
          </Form.Floating>

          <Form.Floating className="mb-3">
            <Form.Control
              id="studentEmail"
              name="email"
              type="email"
              placeholder="Enter email address"
              value={formData.email}
              onChange={handleChange}
              required
              maxLength={100}
              autoComplete="email"
              aria-label="Student email"
            />
            <Form.Label htmlFor="studentEmail">Email *</Form.Label>
          </Form.Floating>

          <Form.Floating className="mb-3">
            <Form.Control
              id="studentPhone"
              name="phone"
              type="tel"
              placeholder="Enter phone number"
              value={formData.phone}
              onChange={handleChange}
              maxLength={20}
              autoComplete="tel"
              aria-label="Student phone"
            />
            <Form.Label htmlFor="studentPhone">Phone</Form.Label>
          </Form.Floating>

          <Form.Floating className="mb-4">
            <Form.Control
              id="studentResumeLink"
              name="resumeLink"
              type="url"
              placeholder="https://example.com/resume.pdf"
              value={formData.resumeLink}
              onChange={handleChange}
              aria-label="Student resume link"
            />
            <Form.Label htmlFor="studentResumeLink">Resume Link</Form.Label>
          </Form.Floating>

          <div className="d-grid">
            <Button
              type="submit"
              variant="primary"
              disabled={loading}
              aria-label="Add Student"
              className="d-flex align-items-center justify-content-center gap-2"
            >
              {loading ? (
                <>
                  <span
                    className="spinner-border spinner-border-sm"
                    role="status"
                    aria-hidden="true"
                  />
                  Adding...
                </>
              ) : (
                <>
                  <PersonPlus />
                  Add Student
                </>
              )}
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default StudentForm;
