import React, { useState, useCallback } from 'react';
import { Card, Form, Button, Alert, ProgressBar, Row, Col, Table, Badge } from 'react-bootstrap';
import { FileEarmarkExcel, Download, CheckCircle, XCircle, InfoCircle } from 'react-bootstrap-icons';
import studentService from '../../services/studentService';

const ExcelUpload = ({ onExcelUploaded }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadResult, setUploadResult] = useState(null);
  const [downloadingTemplate, setDownloadingTemplate] = useState(false);

  const handleFileSelect = useCallback((event) => {
    const file = event.target.files[0];
    
    if (!file) {
      setSelectedFile(null);
      setUploadResult(null);
      return;
    }

    // File validation
    const validTypes = [
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // .xlsx
      'application/vnd.ms-excel' // .xls
    ];

    const maxSize = 10 * 1024 * 1024; // 10MB limit

    if (!validTypes.includes(file.type)) {
      alert('Please select a valid Excel file (.xlsx or .xls)');
      event.target.value = '';
      return;
    }

    if (file.size > maxSize) {
      alert('File size too large. Maximum size allowed is 10MB.');
      event.target.value = '';
      return;
    }

    setSelectedFile(file);
    setUploadResult(null);
  }, []);

  const handleUpload = useCallback(async () => {
    if (!selectedFile) {
      alert('Please select a file first');
      return;
    }

    setUploading(true);
    try {
      const result = await studentService.uploadExcel(selectedFile);
      setUploadResult(result.summary);
      
      if (onExcelUploaded) {
        onExcelUploaded(result.summary);
      }
      
      // Reset file selection
      setSelectedFile(null);
      document.getElementById('excel-upload').value = '';
      
    } catch (error) {
      const errorMessage = error.response?.data?.error || error.message;
      alert('Failed to upload Excel file: ' + errorMessage);
    } finally {
      setUploading(false);
    }
  }, [selectedFile, onExcelUploaded]);

  const downloadSample = useCallback(async () => {
    setDownloadingTemplate(true);
    try {
      const blob = await studentService.downloadSampleExcel();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `student-import-template-${new Date().toISOString().split('T')[0]}.xlsx`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      alert('Failed to download sample template. Please try again.');
    } finally {
      setDownloadingTemplate(false);
    }
  }, []);

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getUploadResultVariant = () => {
    if (!uploadResult) return 'info';
    if (uploadResult.failed === 0 && uploadResult.duplicates === 0) return 'success';
    if (uploadResult.successful > 0) return 'warning';
    return 'danger';
  };

  return (
    <Card className="shadow-sm rounded border-0">
      <Card.Header className="d-flex align-items-center bg-primary text-white">
        <FileEarmarkExcel size={20} className="me-2" /> {/* Reduced from 24 to 20 */}
        <h6 className="mb-0">Bulk Upload Students</h6> {/* Changed from h5 to h6 */}
      </Card.Header>
      <Card.Body className="p-3"> {/* Reduced padding from p-4 to p-3 */}
        {/* Instructions */}
        <Alert variant="info" className="mb-3 border-0" style={{ backgroundColor: '#e7f3ff' }}> {/* Reduced margin */}
          <div className="d-flex align-items-start">
            <InfoCircle className="text-info me-2 mt-1" size={16} /> {/* Reduced from 18 to 16 */}
            <div>
              <strong className="text-info small">Excel Upload Instructions</strong> {/* Added small class */}
              <ul className="mb-0 mt-1" style={{ fontSize: '12px' }}> {/* Smaller font */}
                <li><strong>File formats:</strong> .xlsx or .xls files only</li>
                <li><strong>File size limit:</strong> Maximum 10MB</li>
                <li><strong>Columns:</strong> Name, Email, Phone</li>
              </ul>
            </div>
          </div>
        </Alert>

        <Form noValidate>
          <Form.Group className="mb-3">
            <Form.Label htmlFor="excel-upload" className="fw-bold small"> {/* Added small class */}
              Select Excel File *
            </Form.Label>
            <Form.Control
              id="excel-upload"
              type="file"
              accept=".xlsx,.xls"
              onChange={handleFileSelect}
              disabled={uploading}
              aria-label="Select Excel file"
              size="sm" // Changed from lg to sm
            />
            {selectedFile && (
              <div className="mt-2 p-2 bg-light rounded">
                <div className="d-flex align-items-center text-success">
                  <CheckCircle className="me-2" size={14} /> {/* Reduced from 16 to 14 */}
                  <strong className="small">Selected file:</strong> {/* Added small class */}
                </div>
                <div className="ms-3" style={{ fontSize: '11px' }}> {/* Smaller font */}
                  <div><strong>Name:</strong> {selectedFile.name}</div>
                  <div><strong>Size:</strong> {formatFileSize(selectedFile.size)}</div>
                </div>
              </div>
            )}
          </Form.Group>

          <Row className="g-2 mb-3"> {/* Reduced gap and margin */}
            <Col xs={12} md={6}>
              <Button
                variant="success"
                className="w-100 d-flex align-items-center justify-content-center gap-2 fw-bold small" // Added small class
                onClick={handleUpload}
                disabled={!selectedFile || uploading}
                size="sm" // Changed from lg to sm
              >
                {uploading ? (
                  <>
                    <span className="spinner-border spinner-border-sm" />
                    <span className="small">Processing...</span> {/* Added small class */}
                  </>
                ) : (
                  <>
                    <FileEarmarkExcel size={16} /> {/* Reduced from 18 to 16 */}
                    Upload & Import Students
                  </>
                )}
              </Button>
            </Col>
            <Col xs={12} md={6}>
              <Button
                variant="outline-primary"
                className="w-100 d-flex align-items-center justify-content-center gap-2 fw-bold small" // Added small class
                onClick={downloadSample}
                disabled={downloadingTemplate}
                size="sm" // Changed from lg to sm
              >
                {downloadingTemplate ? (
                  <>
                    <span className="spinner-border spinner-border-sm" />
                    <span className="small">Downloading...</span> {/* Added small class */}
                  </>
                ) : (
                  <>
                    <Download size={16} /> {/* Reduced from 18 to 16 */}
                    Download Template
                  </>
                )}
              </Button>
            </Col>
          </Row>
        </Form>

        {/* Upload Progress */}
        {uploading && (
          <div className="my-3"> {/* Reduced margin */}
            <div className="d-flex justify-content-between align-items-center mb-2">
              <span className="fw-bold small">Processing Excel file...</span> {/* Added small class */}
              <span className="text-muted" style={{ fontSize: '12px' }}>Please wait</span> {/* Smaller font */}
            </div>
            <ProgressBar animated now={100} variant="primary" style={{ height: '6px' }} /> {/* Reduced height */}
          </div>
        )}

        {/* Upload Results */}
        {uploadResult && (
          <Alert
            variant={getUploadResultVariant()}
            className="mt-3 border-0 shadow-sm" // Reduced margin
            dismissible
            onClose={() => setUploadResult(null)}
          >
            <Alert.Heading className="d-flex align-items-center" style={{ fontSize: '16px' }}> {/* Smaller heading */}
              {uploadResult.failed === 0 && uploadResult.duplicates === 0 ? (
                <CheckCircle className="me-2 text-success" size={20} /> /* Reduced from 24 to 20 */
              ) : (
                <XCircle className="me-2 text-warning" size={20} /> /* Reduced from 24 to 20 */
              )}
              Import Results Summary
            </Alert.Heading>
            
            {/* Summary Statistics */}
            <Row className="mb-2"> {/* Reduced margin */}
              <Col sm={3}>
                <div className="text-center">
                  <Badge bg="info" className="px-2 py-1" style={{ fontSize: '11px' }}> {/* Smaller badge */}
                    {uploadResult.total}
                  </Badge>
                  <div style={{ fontSize: '10px' }} className="text-muted mt-1">Total Records</div> {/* Smaller text */}
                </div>
              </Col>
              <Col sm={3}>
                <div className="text-center">
                  <Badge bg="success" className="px-2 py-1" style={{ fontSize: '11px' }}> {/* Smaller badge */}
                    {uploadResult.successful}
                  </Badge>
                  <div style={{ fontSize: '10px' }} className="text-muted mt-1">Successfully Added</div> {/* Smaller text */}
                </div>
              </Col>
              <Col sm={3}>
                <div className="text-center">
                  <Badge bg="warning" className="px-2 py-1" style={{ fontSize: '11px' }}> {/* Smaller badge */}
                    {uploadResult.duplicates || 0}
                  </Badge>
                  <div style={{ fontSize: '10px' }} className="text-muted mt-1">Duplicates Skipped</div> {/* Smaller text */}
                </div>
              </Col>
              <Col sm={3}>
                <div className="text-center">
                  <Badge bg="danger" className="px-2 py-1" style={{ fontSize: '11px' }}> {/* Smaller badge */}
                    {uploadResult.failed}
                  </Badge>
                  <div style={{ fontSize: '10px' }} className="text-muted mt-1">Failed</div> {/* Smaller text */}
                </div>
              </Col>
            </Row>

            {/* Success Message */}
            {uploadResult.successful > 0 && (
              <div className="alert alert-success border-0 py-2 mb-2"> {/* Reduced margin */}
                <strong className="small">✅ {uploadResult.successful} student{uploadResult.successful !== 1 ? 's' : ''} imported successfully!</strong> {/* Added small class */}
                <div style={{ fontSize: '11px' }} className="text-muted mt-1"> {/* Smaller font */}
                  Students can now receive interview invitations.
                </div>
              </div>
            )}

            {/* Error Details */}
            {uploadResult.errors?.length > 0 && (
              <div>
                <strong className="text-danger small">❌ Errors encountered:</strong> {/* Added small class */}
                <div className="mt-2" style={{ maxHeight: '150px', overflowY: 'auto' }}> {/* Reduced height */}
                  <Table size="sm" className="mb-0" style={{ fontSize: '11px' }}> {/* Smaller font */}
                    <thead>
                      <tr>
                        <th>Row</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Error</th>
                      </tr>
                    </thead>
                    <tbody>
                      {uploadResult.errors.slice(0, 10).map((error, index) => (
                        <tr key={index}>
                          <td><Badge bg="secondary" style={{ fontSize: '9px' }}>{error.row}</Badge></td> {/* Smaller badge */}
                          <td>{error.name}</td>
                          <td>{error.email}</td>
                          <td className="text-danger">{error.error}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                  {uploadResult.errors.length > 10 && (
                    <div className="text-center mt-2">
                      <small className="text-muted" style={{ fontSize: '10px' }}> {/* Smaller font */}
                        ... and {uploadResult.errors.length - 10} more errors
                      </small>
                    </div>
                  )}
                </div>
              </div>
            )}
          </Alert>
        )}
      </Card.Body>
    </Card>
  );
};

export default ExcelUpload;
