import api from './api';

const studentService = {
  getStudents: async () => {
    try {
      const response = await api.get('/students');
      return response.data;
    } catch (error) {
      console.error('Failed to get students:', error);
      throw error;
    }
  },

  addStudent: async (studentData) => {
    try {
      // Validate required fields (no resume field)
      if (!studentData.name || !studentData.email || !studentData.phone) {
        throw new Error('Name, email, and phone are required');
      }

      const response = await api.post('/students', {
        name: studentData.name.trim(),
        email: studentData.email.trim().toLowerCase(),
        phone: studentData.phone.trim()
        // No resumeLink field
      });
      
      return response.data;
    } catch (error) {
      console.error('Failed to add student:', error);
      throw error;
    }
  },

  // Get student by token (for slot selection)
  getStudentByToken: async (token) => {
    try {
      if (!token) {
        throw new Error('Token is required');
      }
      const response = await api.get(`/students/by-token/${token}`);
      return response.data;
    } catch (error) {
      console.error('Failed to get student by token:', error);
      throw error;
    }
  },

  // Update resume by token (for slot selection)
  updateResume: async (token, file) => {
    try {
      if (!token) {
        throw new Error('Token is required');
      }
      if (!file) {
        throw new Error('Resume file is required');
      }

      const formData = new FormData();
      formData.append('resume', file);
      
      const response = await api.put(`/students/resume/${token}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data;
    } catch (error) {
      console.error('Failed to update resume:', error);
      throw error;
    }
  },

  // NEW: Download resume method
  downloadResume: async (studentId) => {
    try {
      if (!studentId) {
        throw new Error('Student ID is required');
      }

      console.log('📥 Downloading resume for student ID:', studentId);

      const response = await api.get(`/students/${studentId}/resume/download`, {
        responseType: 'blob', // Important for file downloads
        timeout: 30000, // 30 second timeout for downloads
      });

      // Extract filename from response headers
      const contentDisposition = response.headers['content-disposition'];
      let filename = 'resume.pdf'; // fallback filename
      
      if (contentDisposition) {
        const filenameMatch = contentDisposition.match(/filename[^;=\n]*=((['"]).*?\2|[^;\n]*)/);
        if (filenameMatch && filenameMatch[1]) {
          filename = filenameMatch[1].replace(/['"]/g, '');
        }
      }

      return {
        blob: response.data,
        filename: filename,
        contentType: response.headers['content-type'] || 'application/octet-stream',
        size: response.headers['content-length']
      };
    } catch (error) {
      console.error('Failed to download resume:', error);
      
      if (error.response?.status === 404) {
        throw new Error('Resume not found or student does not exist');
      } else if (error.response?.status === 400) {
        throw new Error('Invalid student ID provided');
      } else if (error.code === 'ECONNABORTED') {
        throw new Error('Download timeout. Please try again.');
      }
      
      throw error;
    }
  },

  // NEW: Get student interview status
  getStudentStatus: async (studentId) => {
    try {
      if (!studentId) {
        throw new Error('Student ID is required');
      }

      const response = await api.get(`/students/${studentId}/status`);
      return response.data;
    } catch (error) {
      console.error('Failed to get student status:', error);
      throw error;
    }
  },

  downloadSampleExcel: async () => {
    try {
      const response = await api.get('/students/sample-template', { 
        responseType: 'blob' 
      });
      return response.data;
    } catch (error) {
      console.error('Failed to download sample Excel:', error);
      throw error;
    }
  },

  uploadExcel: async (file) => {
    try {
      if (!file) {
        throw new Error('No file selected');
      }

      // Validate file type
      const validTypes = [
        'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        'application/vnd.ms-excel'
      ];

      if (!validTypes.includes(file.type)) {
        throw new Error('Invalid file type. Please select an Excel file (.xlsx or .xls)');
      }

      // Validate file size (10MB limit)
      const maxSize = 10 * 1024 * 1024;
      if (file.size > maxSize) {
        throw new Error('File size too large. Maximum size is 10MB');
      }

      const formData = new FormData();
      formData.append('excel', file);

      const response = await api.post('/students/upload-excel', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        timeout: 60000, // 60 second timeout for large files
      });
      
      return response.data;
    } catch (error) {
      console.error('Failed to upload Excel:', error);
      throw error;
    }
  },

  sendInvitations: async (studentIds) => {
    if (!Array.isArray(studentIds) || studentIds.length === 0) {
      throw new Error('studentIds must be a non-empty array');
    }
    try {
      const validIds = studentIds.filter(id => typeof id === 'number' && !isNaN(id));
      if (validIds.length === 0) {
        throw new Error('No valid student IDs to send invitations');
      }

      const response = await api.post('/students/send-invitations', { studentIds: validIds });
      return response.data;
    } catch (error) {
      console.error('Failed to send invitations:', error);
      throw error;
    }
  },

  deleteStudent: async (id) => {
    try {
      const response = await api.delete(`/students/${id}`);
      return response.data;
    } catch (error) {
      console.error('Failed to delete student:', error);
      throw error;
    }
  },

  // Bulk delete students by date range
  deleteByDate: async (start, end) => {
    try {
      if (!start || !end) {
        throw new Error('Start and end dates are required');
      }
      const response = await api.delete(`/students/bulk?start=${start}&end=${end}`);
      return response.data;
    } catch (error) {
      console.error('Failed to bulk delete students:', error);
      throw error;
    }
  },

  // Validate resume file before upload
  validateResumeFile: (file) => {
    const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
    const maxSize = 5 * 1024 * 1024; // 5MB

    if (!file) {
      throw new Error('No file provided');
    }

    if (!allowedTypes.includes(file.type)) {
      throw new Error('Invalid file type. Only PDF, DOC, and DOCX files are allowed');
    }

    if (file.size > maxSize) {
      throw new Error('File size too large. Maximum size is 5MB');
    }

    return true;
  },

  // Helper method to format file size
  formatFileSize: (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  },

  // Helper method to trigger file download in browser
  triggerDownload: (blob, filename) => {
    try {
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      link.style.display = 'none';
      
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      
      // Clean up the URL object
      setTimeout(() => {
        window.URL.revokeObjectURL(url);
      }, 100);
      
      return true;
    } catch (error) {
      console.error('Error triggering download:', error);
      return false;
    }
  },

  // Comprehensive resume download with UI feedback
  downloadResumeWithFeedback: async (studentId, studentName) => {
    try {
      console.log(`📥 Starting resume download for ${studentName} (ID: ${studentId})`);
      
      const { blob, filename, contentType, size } = await studentService.downloadResume(studentId);
      
      console.log(`✅ Resume downloaded: ${filename} (${studentService.formatFileSize(size || blob.size)})`);
      
      // Trigger browser download
      const downloadSuccess = studentService.triggerDownload(blob, filename);
      
      if (!downloadSuccess) {
        throw new Error('Failed to trigger file download');
      }
      
      return {
        success: true,
        filename,
        size: size || blob.size,
        contentType
      };
      
    } catch (error) {
      console.error(`❌ Resume download failed for ${studentName}:`, error);
      throw error;
    }
  },

  // Batch resume downloads (for multiple students)
  downloadMultipleResumes: async (students) => {
    const results = [];
    
    for (const student of students) {
      try {
        const result = await studentService.downloadResumeWithFeedback(student.id, student.name);
        results.push({
          studentId: student.id,
          studentName: student.name,
          success: true,
          ...result
        });
        
        // Add small delay between downloads to prevent overwhelming the server
        await new Promise(resolve => setTimeout(resolve, 500));
        
      } catch (error) {
        results.push({
          studentId: student.id,
          studentName: student.name,
          success: false,
          error: error.message
        });
      }
    }
    
    return results;
  }
};

export default studentService;
